!-----------------------------------------------------------------------! * * * periodic 2d electrostatic particle simulation kernel code * * *! this is a simple 2d skeleton particle-in-cell code designed for! exploring new computer architectures.  it contains the critical pieces! needed for depositing charge and current, advancing particles, and! solving the fields.  the code moves only electrons, with periodic! electromagnetic forces obtained by solving maxwells's equation with! fast fourier transforms.! the only diagnostic is particle and field energy.! portable gcpic kernel code, using algorithm described in:! p. c. liewer and v. k. decyk, j. computational phys. 85, 302 (1989).! written by viktor k. decyk, ucla! for mpi distributed memory multiprocessing macintosh g4 computers! update: march 8, 2003      program pbbeps2      use pinit2d!     use mpbpush2d!     use mppush2d      use pbpush2d      use ppush2d      use pfield2d      use p2d      implicit none! idps = number of partition boundaries! idimp = dimension of phase space = 5! mshare = (0,1) = (no,yes) architecture is shared memory! ipbc = particle boundary condition = (0,1,2,3) =! (none,2d periodic,2d reflecting,mixed reflecting/periodic)      integer :: idps =    2, idimp =   5, mshare =   0, ipbc = 1      integer :: sortime = 50, vect = 0      integer :: npxy, npxyb, np, nx, ny, nxh, nyh, nyv, nxe, nxeh      integer :: nloop, nvp, nblok, npmax, nypmx      integer :: kyp, kxp, kyb, kxb, kxyb, kbmin, kblok, jbmin, jblok      integer :: ngds, nxyh, nxhy, nbmax      integer :: idproc, id0, kstrt, itime, isign, ierr      integer :: it, nx1, lprec, nrec      integer :: ntasks      real :: zero = 0.0      real :: qbme, affp, dth, qi0, we, wf, wm, wke, wef! omx/omy/omz = magnetic field electron cyclotron frequency in x/y/z ! direction      real :: omx = 0.2, omy = 0.5, omz = 1.0      double precision :: dtime      real, dimension(:,:,:), allocatable :: part      real, dimension(:,:,:), allocatable :: qe      real, dimension(:,:,:,:), allocatable :: cu, fxyze, bxyze      complex, dimension(:,:,:,:), allocatable :: exyz, bxyz      complex, dimension(:,:,:), allocatable :: qt      complex, dimension(:,:,:,:), allocatable :: cut, fxyt, bxyt      complex, dimension(:,:,:), allocatable :: ffc      integer, dimension(:), allocatable :: mixup      complex, dimension(:), allocatable :: sct      real, dimension(:,:), allocatable  :: edges      integer, dimension(:), allocatable :: nyp, noff      integer, dimension(:), allocatable :: npp, nps      complex, dimension(:,:,:), allocatable :: pott      real, dimension(:,:,:), allocatable :: pot! wtot = total energy      real, dimension(6) :: wtot! time = timing array      real, dimension(2) :: time! msg = heartbeat array      double precision, dimension(6) :: msg      character(len=16) :: fname      character(len=12) :: label, potname      character(len=4) :: nlabel  991 format (5h t = ,i7)  992 format (19h * * * q.e.d. * * *)  993 format (34h field, kinetic, total energies = ,3e14.7)  994 format (36h electric(l,t), magnetic energies = ,3e14.7)! nvp = number of real or virtual processors! initialize for parallel processing      call PPINIT(idproc,id0,nvp)      kstrt = idproc + 1! read namelist      if (id0==0) then         open(unit=8,file='pinput2',form='formatted',status='old')         read (8,pinput2)         open(unit=18,file='poutput2',form='formatted',status='unknown')         write (18,pinput2)      endif! broadcast namelist to other nodes      call sendnml()! np = total number of electrons in simulation      npxy = npx*npy; npxyb = npxb*npyb; np = npxy + npxyb      nx = 2**indx; ny = 2**indy; nxh = nx/2; nyh = ny/2      nxe = nx + 4; nyv = ny + 2! kyp = number of complex grids in each field partition in y direction! nypmx = maximum size of particle partition, including guard cells.      kyp = (ny - 1)/nvp + 1; nypmx = kyp + 3! ngds = number of guard cells      ngds = 3*((idps - 1)/2 + 1)      if (inorder==LINEAR) then         ax = .912871; ay = .912871         nxe = nx + 2; ; nypmx = kyp + 1         ngds = (idps - 1)/2 + 1      endif! check if too many processors      if (nvp > ny) then         write (2,*) 'Too many processors requested, ny, nvp=', ny, nvp         call PPEXIT         stop      endif! initialize for multiprocessing!     call mpinit(ntasks)      write (18,*) ntasks+1, ' processors found and used'      if (popt==VECTOR) vect = 1      nxeh = nxe/2! nloop = number of time steps in simulation      nloop = tend/dt + .0001! nblok = number of particle partitions      nblok = 1 + mshare*(nvp - 1)! npmax = maximum number of particles in each partition      npmax = (np/nvp)*1.01 + 7000! kxp = number of complex grids in each field partition in x direction      kxp = (nxh - 1)/nvp + 1! kyb = number of processors in y! kxb = number of processors in x      kyb = ny/kyp; kxb = nxh/kxp! kxyb = maximum(kxb,kyb)      kxyb = max(kxb,kyb)! kblok = number of field partitions in y direction      kbmin = 1 + (1 - mshare)*(kxyb/kxb - 1)      kblok = 1 + mshare*(ny/kyp - 1)! jblok = number of field partitions in x direction      jbmin = 1 + (1 - mshare)*(kxyb/kyb - 1)      jblok = 1 + mshare*(nxh/kxp - 1)! nxyh = maximum(nx,ny)/2      nxyh = max(nx,ny)/2! nxhy = maximum(nx/2,ny)      nxhy = max(nxh,ny)! nbmax = size of buffer for passing particles between processors      nbmax = 1 + (2*(npxy*vty + npxyb*vtdy) + 1.4*npxyb*abs(vdy))*dt/ny! diagnostics! potential diagnostics      if (ntp > 0) then         nx1 = nx + 1         allocate(pot(nx1,kyp,kblok))         inquire(iolength=lprec) pot         potname = 'pot2'         if (id0==0) call PHEAD2(10,nx1,kyp,nvp,trim(potname))      endif!! diagnostic nodes have special processing      if (idproc < 0) call diag2nodes!! part(1,n,l) = position x of particle n in partition l! part(2,n,l) = position y of particle n in partition l! part(3,n,l) = velocity vx of particle n in partition l! part(4,n,l) = velocity vy of particle n in partition l      allocate(part(idimp,npmax,nblok))! in real space, qe(j+1,k,l) = charge density at grid point (j,kk)! in real space, cu(3,j+1,k,l) = current density at grid point (j,kk)      allocate(qe(nxe,nypmx*kbmin,kblok),cu(3,nxe,nypmx*kbmin,kblok))! in real space, fxyze(i,j+1,k,l) = i component of force/charge at ! grid point (j,kk)! in other words, fxyze are the convolutions of the electric field! over the particle shape, where kk = k + noff(l) - 1      allocate(fxyze(3,nxe,nypmx*kbmin,kblok))! bxyze(i,j+1,k,l) = i component of magnetic field at grid point (j,kk)      allocate(bxyze(3,nxe,nypmx*kbmin,kblok))! in fourier space, exyz = transverse electric field! in fourier space, bxyz = magnetic field          allocate(exyz(3,nyv,kxp,jblok),bxyz(3,nyv,kxp,jblok))! qt(k,j,l) = complex charge density for fourier mode jj-1,k-1! cut(k,j,l) = complex current density for fourier mode jj-1,k-1      allocate(qt(nyv,kxp,jblok),cut(3,nyv,kxp,jblok))! fxyt(i,k,j,l) = i component of force/charge for fourier mode jj-1,k-1! bxyt(i,k,j,l) = i component of magnetic field for mode jj-1,k-1! where jj = j + kxp*(l - 1)      allocate(fxyt(3,nyv,kxp,jblok),bxyt(3,nyv,kxp,jblok))! ffc = form factor array for poisson solver      allocate(ffc(nyh,kxp,jblok))! mixup, sct = arrays for fft      allocate(mixup(nxhy),sct(nxyh))! edges(1,l) = lower boundary of particle partition l! edges(2,l) = upper boundary of particle partition l      allocate(edges(idps,nblok))! nyp(l) = number of primary gridpoints in particle partition l.! noff(l) = lowermost global gridpoint in particle partition l.      allocate(nyp(nblok),noff(nblok))! npp(l) = number of particles in partition l! nps(l) = starting address of particles in partition l      allocate(npp(nblok),nps(nblok))! potential      if (ntp > 0) allocate(pott(nyv,kxp,jblok))! initialize parallel timer      call pwtimer(time,dtime,-1)! initialize constants      itime = 0      qbme = qme      affp = float(nx*ny)/float(np)      dth = .5*dt! calculate partition variables      call dcomp(edges,nyp,noff,ny,kstrt,nvp,inorder)      bxyze = 0.      bxyz = cmplx(0.,0.)      exyz = cmplx(0.,0.)      call bguard(bxyze,nyp,nx,inorder)! prepare fft tables      call fft_init(mixup,sct,indx,indy)! calculate form factors      call pois_init(ffc,ax,ay,affp,nx,ny,kstrt)! initialize density profile and velocity distribution! background electrons      nps = 1      npp = 0!     if (npxy > 0) call distr(part,edges,npp,nps,vtx,vty,zero,zero,npx,&!    &npy,nx,ny,ipbc)      if (npxy > 0) call distr(part,edges,npp,nps,vtx,vty,vtz,zero,zero,&     &zero,npx,npy,nx,ny,ipbc)! beam electrons      nps = npp + 1!     if (npxyb > 0) call distr(part,edges,npp,nps,vtdx,vtdy,vdx,vdy,npx&!    &b,npyb,nx,ny,ipbc)      if (npxyb > 0) call distr(part,edges,npp,nps,vtdx,vtdy,vtdz,vdx,vd&     &y,vdz,npxb,npyb,nx,ny,ipbc)! calculate actual coordinates from guiding centers      call distr(part,bxyze,npp,noff,qbme,nx,ny,ipbc,inorder)      call retard(part,npp,dth,nx,ny,ipbc)! move particles into appropriate spatial regions      call pmove(part,edges,npp,ny,kstrt,nvp,nbmax,vect,ierr)      if (ierr /= 0) then!        call MP_END         call PPEXIT         stop      endif! initialize current density to background      call sguard(cu,nyp,zero,zero,zero,nx,inorder)! deposit current for initial distribution      call djpost(part,cu,npp,noff,qme,dth,nx,ny,ipbc,inorder,STANDARD)!     call djpost(part,cu,npp,nps,noff,qme,dth,nx,ny,ipbc,ntasks,inorder&!    &,STANDARD)! move particles into appropriate spatial regions      call pmove(part,edges,npp,ny,kstrt,nvp,nbmax,vect,ierr)      if (ierr /= 0) then!        call MP_END         call PPEXIT         stop      endif! add guard cells in x direction      call aguard(cu,nyp,nx,inorder)! copy data from particle to field partition, and add up guard cells in y      call paguard(cu,kstrt,nvp,kyp,ngds)! initialize charge density to background      qi0 = -qme/affp      call sguard(qe,nyp,qi0,nx,inorder)! deposit charge for initial distribution      call dpost(part,qe,qme,npp,noff,inorder,STANDARD)!     call dpost(part,qe,qme,npp,nps,noff,ntasks,inorder,STANDARD)! add guard cells in x direction      call aguard(qe,nyp,nx,inorder)! copy data from particle to field partition, and add up guard cells in y      call paguard(qe,kstrt,nvp,kyp,ngds)      call pwtimer(time,dtime)! send initial CPU Time to diagnostic nodes      msg(1) = time(1); msg(2) = time(2)      call HARTBEAT(msg,2)      if (id0==0) then         write (18,*) 'init max/min real time=', time(1), time(2), 'sec'      endif!! * * * start main iteration loop * * *!  500 if (nloop <= itime) go to 2000! send time step to diagnostic nodes      msg(1) = itime      call HARTBEAT(msg,1)      if (id0==0) write (18,991) itime      write (label,991) itime      call LOGNAME(label)! transform charge to fourier space      isign = -1      call fft(qe,qt,isign,mixup,sct,indx,indy,kstrt,kyp,inorder)! transform current to fourier space      isign = -1      call fft(cu,cut,isign,mixup,sct,indx,indy,kstrt,kyp,inorder)! take transverse part of current      call cuperp(cut,nx,ny,kstrt)! potential diagnostic      if (ntp > 0) then         it = itime/ntp         if (itime==ntp*it) then! calculate potential in fourier space            call pois(qt,pott,ffc,we,nx,ny,kstrt)! transform potential to real space (result stored in qe)            isign = 1            call fft(qe,pott,isign,mixup,sct,indx,indy,kstrt,kyp,inorder&     &)            call cguard(qe,nyp,nx,LINEAR)            pot = qe(1:nx1,1:kyp,:)! write diagnostic output            write (nlabel,'(i4)') it            if (nlabel(1:1)==' ') nlabel(1:1) = '0'            if (nlabel(2:2)==' ') nlabel(2:2) = '0'            if (nlabel(3:3)==' ') nlabel(3:3) = '0'            fname = trim(potname)//'_'//trim(nlabel)            nrec = -lprec!           call writef(pot,10,nrec,trim(fname))         endif      endif! calculate force/charge in fourier space      call pois3(qt,fxyt,ffc,we,nx,ny,kstrt)! calculate magnetic field in fourier space      if (itime==0) then         call ibpois(cut,bxyz,ffc,ci,wm,nx,ny,kstrt)         wf = 0.      else         call maxwel(exyz,bxyz,cut,ffc,affp,ci,dt,wf,wm,nx,ny,kstrt)      endif! add longitudinal and transverse electric fields      isign = 1      call emfield(fxyt,exyz,ffc,isign,nx,ny,kstrt)! copy magnetic field      isign = -1      call emfield(bxyt,bxyz,ffc,isign,nx,ny,kstrt)! transform force/charge to real space      isign = 1      call fft(fxyze,fxyt,isign,mixup,sct,indx,indy,kstrt,kyp,inorder)! copy data from field to particle partition, and copy to guard cells      call pcguard(fxyze,kstrt,nvp,kyp,inorder)      call bguard(fxyze,nyp,nx,inorder)! transform magnetic field to real space      isign = 1      call fft(bxyze,bxyt,isign,mixup,sct,indx,indy,kstrt,kyp,inorder)! copy data from field to particle partition, and copy to guard cells      call pcguard(bxyze,kstrt,nvp,kyp,inorder)      call bguard(bxyze,nyp,nx,inorder)! particle push and charge density update      wke = 0.! push particles!     call push(part,fxyze,npp,noff,qbme,dt,wke,nx,ny,ipbc,inorder,popt)!     call push(part,fxyze,npp,nps,noff,qbme,dt,wke,nx,ny,ipbc,ntasks,in&!    &order,popt)!     call push(part,fxyze,bxyze,npp,noff,qbme,dt,dth,wke,nx,ny,ipbc,ino&!    &rder,popt)!     call push(part,fxyze,bxyze,npp,nps,noff,qbme,dt,dth,wke,nx,ny,ipbc&!    &,ntasks,inorder,popt)      call push3(part,fxyze,bxyze,npp,noff,qbme,dt,dth,wke,nx,ny,ipbc,in&     &order,popt)!     call push3(part,fxyze,bxyze,npp,nps,noff,qbme,dt,dth,wke,nx,ny,ipb&!    &c,ntasks,inorder,popt)! move particles into appropriate spatial regions      call pmove(part,edges,npp,ny,kstrt,nvp,nbmax,vect,ierr)      if (ierr /= 0) then!        call MP_END         call PPEXIT         stop      endif! initialize current density to background      call sguard(cu,nyp,zero,zero,zero,nx,inorder)! deposit current for initial distribution      call djpost(part,cu,npp,noff,qme,dth,nx,ny,ipbc,inorder,djopt)!     call djpost(part,cu,npp,nps,noff,qme,dth,nx,ny,ipbc,ntasks,inorder&!    &,djopt)! move particles into appropriate spatial regions      call pmove(part,edges,npp,ny,kstrt,nvp,nbmax,vect,ierr)      if (ierr /= 0) then!        call MP_END         call PPEXIT         stop      endif! add guard cells in x direction      call aguard(cu,nyp,nx,inorder)! copy data from particle to field partition, and add up guard cells in y      call paguard(cu,kstrt,nvp,kyp,ngds)! initialize charge density to background      call sguard(qe,nyp,qi0,nx,inorder)! deposit charge      call dpost(part,qe,qme,npp,noff,inorder,dopt)!     call dpost(part,qe,qme,npp,nps,noff,ntasks,inorder,dopt)! merge density arrays in x direction      call aguard(qe,nyp,nx,inorder)! copy data from particle to field partition, and add up guard cells in y      call paguard(qe,kstrt,nvp,kyp,ngds)! sort particles      if (mod(itime,sortime)==0) then         call sortp(part,npp,noff,kyp,inorder)      endif ! energy diagnostic      if (ntw > 0) then         it = itime/ntw         if (itime==ntw*it) then            wef = we + wf + wm            wtot(1) = wef            wtot(2) = wke            wtot(3) = wef + wke            wtot(4) = we            wtot(5) = wf            wtot(6) = wm            call plsum(wtot)! send energy values to diagnostic node            msg = wtot            call HARTBEAT(msg,6)            if (id0==0) write (18,993) wtot(1), wtot(2), wtot(3)            if (id0==0) write (18,994) wtot(4), wtot(5), wtot(6)         endif      endif      itime = itime + 1      go to 500 2000 continue!! * * * end main iteration loop * * *!! send QUIT message to diagnostic nodes      msg = -1.      call HARTBEAT(msg,1)      call pwtimer(time,dtime)! send main CPU Time to diagnostic nodes      msg(1) = time(1); msg(2) = time(2)      call HARTBEAT(msg,2)      if (id0==0) then         write (18,*) 'main max/min real time=', time(1), time(2), 'sec'         write (18,992)       endif!     call MP_END      call PPEXIT      stop!      contains!         subroutine diag2nodes         implicit none! diagnostic nodes have special processing  991    format (5h t = ,i7)  992    format (19h * * * q.e.d. * * *)  993    format (34h field, kinetic, total energies = ,3e14.7)  994 format (36h electric(l,t), magnetic energies = ,3e14.7)! get initial CPU Time         call HARTBEAT(msg,2)         time(1) = msg(1); time(2) = msg(2)         if (id0==0) then         write (18,*) 'init max/min real time=', time(1), time(2), 'sec'         endif! get time step   10    call HARTBEAT(msg,1)         itime = msg(1)         if (itime < 0) then! get main CPU Time            call HARTBEAT(msg,2)            time(1) = msg(1); time(2) = msg(2)            if (id0==0) then               write (18,*) 'main max/min real time=', time(1), time(2),&     &'sec'               write (18,992)             endif!           call MP_END            call PPEXIT            stop         endif         if (id0==0) write (18,991) itime         write (label,991) itime         call LOGNAME(label)! potential diagnostic         if (ntp > 0) then            it = itime/ntp            if (itime==ntp*it) then               write (nlabel,'(i4)') it               if (nlabel(1:1)==' ') nlabel(1:1) = '0'               if (nlabel(2:2)==' ') nlabel(2:2) = '0'               if (nlabel(3:3)==' ') nlabel(3:3) = '0'               fname = trim(potname)//'_'//trim(nlabel)               nrec = -lprec               call writef(pot,10,nrec,trim(fname))               close(unit=10)            endif         endif! energy diagnostic         if (ntw > 0) then            it = itime/ntw            if (itime==ntw*it) then! get energy values               call HARTBEAT(msg,6)               wtot = msg               if (id0==0) write (18,993) wtot(1), wtot(2), wtot(3)               if (id0==0) write (18,994) wtot(4), wtot(5), wtot(6)            endif         endif         go to 10      end subroutine!      end program pbbeps2