!-----------------------------------------------------------------------!      module pinit2d!! Fortran90 interface to 2d parallel PIC Fortran77 library pinit2lib.f! written by viktor k. decyk, ucla! copyright 1999, regents of the university of california! update: june 30, 2000!      implicit none!     private      public :: distr!! define interface to original Fortran77 procedures      interface         subroutine PISTR2(part,edges,npp,nps,vtx,vty,vdx,vdy,npx,npy,nx&     &,ny,idimp,npmax,nblok,idps)         implicit none         integer :: npx, npy, nx, ny, idimp, npmax, nblok, idps         real :: vtx, vty, vdx, vdy         real, dimension(idimp,npmax,nblok) :: part         real, dimension(idps,nblok) :: edges         integer, dimension(nblok) :: npp, nps         end subroutine      end interface!! define generic interfaces to Fortran90 library!      interface distr         module procedure ipistr2      end interface!! define Fortran90 interface functions to Fortran77 library!      contains!         subroutine ipistr2(part,edges,npp,nps,vtx,vty,vdx,vdy,npx,npy,n&     &x,ny)         implicit none         integer :: npx, npy, nx, ny         real :: vtx, vty, vdx, vdy         real, dimension(:,:,:) :: part         real, dimension(:,:) :: edges         integer, dimension(:) :: npp, nps         integer :: idimp, npmax, nblok, idps         idimp = size(part,1); npmax = size(part,2)         nblok = size(part,3); idps = size(edges,1)         call PISTR2(part,edges,npp,nps,vtx,vty,vdx,vdy,npx,npy,nx,ny,id&     &imp,npmax,nblok,idps)         end subroutine ipistr2!      end module pinit2d