te flop count is: 8*nxc*nyc + 4*(nxc + nyc)c where nxc = (nx/2-1)/nvp, nyc = ny/2 - 1, and nvp = number of procsc if isign = 0, form factor array is preparedc if isign < 0, magnetic field is calculated using the equations:c bz(kx,ky) = ci*ci*sqrt(-1)*g(kx,ky)*(kx*cuy(kx,ky)-ky*cux(kx,ky))*c             s(kx,ky),c where kx = 2pi*j/nx, ky = 2pi*k/ny, and j,k = fourier mode numbers,c g(kx,ky) = (affp/(kx**2+ky**2))*s(kx,ky),c s(kx,ky) = exp(-((kx*ax)**2+(ky*ay)**2)/2), except forc bz(kx=pi) = 0, bz(ky=pi) = 0, bz(kx=0,ky=0) = 0.c if isign = 1, vector potential is calculated using the equation:c bx(kx,ky) = ci*ci*g(kx,ky)*cux(kx,ky)c by(kx,ky) = ci*ci*g(kx,ky)*cuy(kx,ky)c if isign = 2, smoothing is calculated using the equation:c bx(kx,ky) = cux(kx,ky)*s(kx,ky)c by(kx,ky) = cuy(kx,ky)*s(kx,ky)c cu(i,k,j,l) = i-th component of complex current density andc bxy(i,k,j,l) = i-th component of complex vector potential,c bz(k,j,l) = z component of complex magnetic field,c for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c jblok = number of data blocksc kxp = number of data values per blockc kstrt = starting data block numberc aimag(ffc(k,j,l)) = finite-size particle shape factor sc real(ffc(k,j,l)) = potential green's function gc for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c ax/ay = half-width of particle in x/y directionc affp = normalization constant = nx*ny/np, where np=number of particlesc ci = reciprical of velocity of lightc magnetic field energy is also calculated, usingc wm = nx*ny*nz*sum((affp/(kx**2+ky**2+kz**2))*ci*cic    |cu(kx,ky,kz)*s(kx,ky,kz)|**2)c this expression is valid only if the current is divergence-freec nx/ny = system length in x/y directionc nyv = first dimension of field arrays, must be >= nyc nyhd = first dimension of form factor array, must be >= nyh      double precision wp      complex cu, bxy, bz, ffc, zero, zt2, zt3      dimension cu(2,nyv,kxp,jblok)      dimension bxy(2,nyv,kxp,jblok), bz(nyv,kxp,jblok)      dimension ffc(nyhd,kxp,jblok)      nxh = nx/2      nyh = ny/2      ny2 = ny + 2      ks = kstrt - 2      dnx = 6.28318530717959/float(nx)      dny = 6.28318530717959/float(ny)      zero = cmplx(0.,0.)      ci2 = ci*ci      if (isign.ne.0) go to 40      if (kstrt.gt.nxh) returnc prepare form factor array      do 30 l = 1, jblok      joff = kxp*(l + ks) - 1      do 20 j = 1, kxp      dkx = dnx*float(j + joff)      at1 = dkx*dkx      at2 = (dkx*ax)**2      do 10 k = 1, nyh      dky = dny*float(k - 1)      at3 = dky*dky + at1      at4 = exp(-.5*((dky*ay)**2 + at2))      if (at3.eq.0.) then         ffc(k,j,l) = cmplx(affp,1.)      else         ffc(k,j,l) = cmplx(affp*at4/at3,at4)      endif   10 continue   20 continue   30 continue      return   40 if (isign.gt.0) go to 100c calculate magnetic field and sum field energy      wp = 0.0d0      if (kstrt.gt.nxh) go to 90      do 80 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 60 j = 1, kxp      dkx = dnx*float(j + joff)      if ((j+joff).gt.0) then         do 50 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at1 = ci2*real(ffc(k,j,l))*aimag(ffc(k,j,l))         at2 = dky*at1         at3 = dkx*at1         zt2 = cmplx(-aimag(cu(2,k,j,l)),real(cu(2,k,j,l)))         zt3 = cmplx(-aimag(cu(1,k,j,l)),real(cu(1,k,j,l)))         bz(k,j,l) = at3*zt2 - at2*zt3         zt2 = cmplx(-aimag(cu(2,k1,j,l)),real(cu(2,k1,j,l)))         zt3 = cmplx(-aimag(cu(1,k1,j,l)),real(cu(1,k1,j,l)))         bz(k1,j,l) = at3*zt2 + at2*zt3         wp = wp + at1*(cu(1,k,j,l)*conjg(cu(1,k,j,l)) + cu(2,k,j,l)*con     1jg(cu(2,k,j,l)) + cu(1,k1,j,l)*conjg(cu(1,k1,j,l)) + cu(2,k1,j,l)*     2conjg(cu(2,k1,j,l)))   50    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at1 = ci2*real(ffc(1,j,l))*aimag(ffc(1,j,l))         at2 = dkx*at1         zt2 = cmplx(-aimag(cu(2,1,j,l)),real(cu(2,1,j,l)))         bz(1,j,l) = at2*zt2         bz(k1,j,l) = zero         wp = wp + at1*(cu(1,1,j,l)*conjg(cu(1,1,j,l)) + cu(2,1,j,l)*con     1jg(cu(2,1,j,l)))      endif   60 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 70 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at1 = ci2*real(ffc(k,1,l))*aimag(ffc(k,1,l))         at2 = dky*at1         zt2 = cmplx(-aimag(cu(1,k,1,l)),real(cu(1,k,1,l)))         bz(k,1,l) = -at2*zt2         bz(k1,1,l) = zero         wp = wp + at1*(cu(1,k,1,l)*conjg(cu(1,k,1,l)) + cu(2,k,1,l)*con     1jg(cu(2,k,1,l)))   70    continue         k1 = nyh + 1         bz(1,1,l) = zero         bz(k1,1,l) = zero      endif   80 continue   90 continue      wm = float(nx*ny)*wp      returnc calculate vector potential and sum field energy  100 if (isign.gt.1) go to 160      wp = 0.0d0      if (kstrt.gt.nxh) go to 150      do 140 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 120 j = 1, kxp      if ((j+joff).gt.0) then         do 110 k = 2, nyh         k1 = ny2 - k         at2 = ci2*real(ffc(k,j,l))         at1 = at2*aimag(ffc(k,j,l))         bxy(1,k,j,l) = at2*cu(1,k,j,l)         bxy(2,k,j,l) = at2*cu(2,k,j,l)         bxy(1,k1,j,l) = at2*cu(1,k1,j,l)         bxy(2,k1,j,l) = at2*cu(2,k1,j,l)         wp = wp + at1*(cu(1,k,j,l)*conjg(cu(1,k,j,l)) + cu(2,k,j,l)*con     1jg(cu(2,k,j,l)) + cu(1,k1,j,l)*conjg(cu(1,k1,j,l)) + cu(2,k1,j,l)*     2conjg(cu(2,k1,j,l)))  110    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at2 = ci2*real(ffc(1,j,l))         at1 = at2*aimag(ffc(1,j,l))         bxy(1,1,j,l) = at2*cu(1,1,j,l)         bxy(2,1,j,l) = at2*cu(2,1,j,l)         bxy(1,k1,j,l) = zero         bxy(2,k1,j,l) = zero         wp = wp + at1*(cu(1,1,j,l)*conjg(cu(1,1,j,l)) + cu(2,1,j,l)*con     1jg(cu(2,1,j,l)))      endif  120 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 130 k = 2, nyh         k1 = ny2 - k         at2 = ci2*real(ffc(k,1,l))         at1 = at2*aimag(ffc(k,1,l))         bxy(1,k,1,l) = at2*cu(1,k,1,l)         bxy(2,k,1,l) = at2*cu(2,k,1,l)         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero         wp = wp + at1*(cu(1,k,1,l)*conjg(cu(1,k,1,l)) + cu(2,k,1,l)*con     1jg(cu(2,k,1,l)))  130    continue         k1 = nyh + 1         bxy(1,1,1,l) = zero         bxy(2,1,1,l) = zero         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero      endif  140 continue  150 continue      wm = float(nx*ny)*wp      returnc calculate smoothing  160 if (kstrt.gt.nxh) go to 210      do 200 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 180 j = 1, kxp      if ((j+joff).gt.0) then         do 170 k = 2, nyh         k1 = ny2 - k         at1 = aimag(ffc(k,j,l))         bxy(1,k,j,l) = at1*cu(1,k,j,l)         bxy(2,k,j,l) = at1*cu(2,k,j,l)         bxy(1,k1,j,l) = at1*cu(1,k1,j,l)         bxy(2,k1,j,l) = at1*cu(2,k1,j,l)  170    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at1 = aimag(ffc(1,j,l))         bxy(1,1,j,l) = at1*cu(1,1,j,l)         bxy(2,1,j,l) = at1*cu(2,1,j,l)         bxy(1,k1,j,l) = zero         bxy(2,k1,j,l) = zero      endif  180 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 190 k = 2, nyh         k1 = ny2 - k         at1 = aimag(ffc(k,1,l))         bxy(1,k,1,l) = at1*cu(1,k,1,l)         bxy(2,k,1,l) = at1*cu(2,k,1,l)         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero  190    continue         k1 = nyh + 1         at1 = aimag(ffc(1,1,l))         bxy(1,1,1,l) = cmplx(at1*real(cu(1,1,1,l)),0.)         bxy(2,1,1,l) = cmplx(at1*real(cu(2,1,1,l)),0.)         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero      endif  200 continue  210 continue      return      endc-----------------------------------------------------------------------      subroutine IPBPOISP23(cu,bxy,ffc,ci,wm,nx,ny,kstrt,nyv,kxp,jblok,n     1yhd)c this subroutine solves 2-1/2d poisson's equation in fourier space forc magnetic field with periodic boundary conditions for distributed data.c input: cu,ffc,ci,nx,ny,kstrt,nyv,kxp,jblok,nyhd, output: bxy,wmc approximate flop count is: 85*nxc*nyc + 36*(nxc + nyc)c where nxc = (nx/2-1)/nvp, nyc = ny/2 - 1, and nvp = number of procsc magnetic field is calculated using the equations:c bx(kx,ky) = ci*ci*sqrt(-1)*g(kx,ky)*ky*cuz(kx,ky),c by(kx,ky) = -ci*ci*sqrt(-1)*g(kx,ky)*kx*cuz(kx,ky),c bz(kx,ky) = ci*ci*sqrt(-1)*g(kx,ky)*(kx*cuy(kx,ky)-ky*cux(kx,ky)),c where kx = 2pi*j/nx, ky = 2pi*k/ny, and j,k = fourier mode numbers,c g(kx,ky) = (affp/(kx**2+ky**2))*s(kx,ky),c s(kx,ky) = exp(-((kx*ax)**2+(ky*ay)**2)/2), except forc bx(kx=pi) = by(kx=pi) = bz(kx=pi) = 0,c bx(ky=pi) = by(ky=pi) = bz(ky=pi) = 0,c bx(kx=0,ky=0) = by(kx=0,ky=0) = bz(kx=0,ky=0) = 0.c cu(i,k,j,l) = i-th component of complex current density andc bxy(i,k,j,l) = i-th component of complex magnetic field,c for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c jblok = number of data blocksc kxp = number of data values per blockc kstrt = starting data block numberc aimag(ffc(k,j,l)) = finite-size particle shape factor sc real(ffc(k,j,l)) = potential green's function gc for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c ci = reciprical of velocity of lightc magnetic field energy is also calculated, usingc wm = nx*ny*nz*sum((affp/(kx**2+ky**2+kz**2))*ci*cic    |cu(kx,ky,kz)*s(kx,ky,kz)|**2), wherec affp = normalization constant = nx*ny/np, where np=number of particlesc this expression is valid only if the current is divergence-freec nx/ny = system length in x/y directionc nyv = first dimension of field arrays, must be >= nyc nyhd = first dimension of form factor array, must be >= nyh      double precision wp      complex cu, bxy, ffc, zero, zt1, zt2, zt3      dimension cu(3,nyv,kxp,jblok)      dimension bxy(3,nyv,kxp,jblok)      dimension ffc(nyhd,kxp,jblok)      nxh = nx/2      nyh = ny/2      ny2 = ny + 2      ks = kstrt - 2      dnx = 6.28318530717959/float(nx)      dny = 6.28318530717959/float(ny)      zero = cmplx(0.,0.)      ci2 = ci*cic calculate magnetic field and sum field energy      wp = 0.0d0      if (kstrt.gt.nxh) go to 50      do 40 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 20 j = 1, kxp      dkx = dnx*float(j + joff)      if ((j+joff).gt.0) then         do 10 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at1 = ci2*real(ffc(k,j,l))         at2 = dky*at1         at3 = dkx*at1         at1 = at1*aimag(ffc(k,j,l))         zt1 = cmplx(-aimag(cu(3,k,j,l)),real(cu(3,k,j,l)))         zt2 = cmplx(-aimag(cu(2,k,j,l)),real(cu(2,k,j,l)))         zt3 = cmplx(-aimag(cu(1,k,j,l)),real(cu(1,k,j,l)))         bxy(1,k,j,l) = at2*zt1         bxy(2,k,j,l) = -at3*zt1         bxy(3,k,j,l) = at3*zt2 - at2*zt3         zt1 = cmplx(-aimag(cu(3,k1,j,l)),real(cu(3,k1,j,l)))         zt2 = cmplx(-aimag(cu(2,k1,j,l)),real(cu(2,k1,j,l)))         zt3 = cmplx(-aimag(cu(1,k1,j,l)),real(cu(1,k1,j,l)))         bxy(1,k1,j,l) = -at2*zt1         bxy(2,k1,j,l) = -at3*zt1         bxy(3,k1,j,l) = at3*zt2 + at2*zt3         wp = wp + at1*(cu(1,k,j,l)*conjg(cu(1,k,j,l)) + cu(2,k,j,l)*con     1jg(cu(2,k,j,l)) + cu(3,k,j,l)*conjg(cu(3,k,j,l)) + cu(1,k1,j,l)*co     2njg(cu(1,k1,j,l)) + cu(2,k1,j,l)*conjg(cu(2,k1,j,l)) + cu(3,k1,j,l     3)*conjg(cu(3,k1,j,l)))   10    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at1 = ci2*real(ffc(1,j,l))         at2 = dkx*at1         at1 = at1*aimag(ffc(1,j,l))         zt1 = cmplx(-aimag(cu(3,1,j,l)),real(cu(3,1,j,l)))         zt2 = cmplx(-aimag(cu(2,1,j,l)),real(cu(2,1,j,l)))         bxy(1,1,j,l) = zero         bxy(2,1,j,l) = -at2*zt1         bxy(3,1,j,l) = at2*zt2         bxy(1,k1,j,l) = zero         bxy(2,k1,j,l) = zero         bxy(3,k1,j,l) = zero         wp = wp + at1*(cu(1,1,j,l)*conjg(cu(1,1,j,l)) + cu(2,1,j,l)*con     1jg(cu(2,1,j,l)) + cu(3,1,j,l)*conjg(cu(3,1,j,l)))      endif   20 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 30 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at1 = ci2*real(ffc(k,1,l))         at2 = dky*at1         at1 = at1*aimag(ffc(k,1,l))         zt1 = cmplx(-aimag(cu(3,k,1,l)),real(cu(3,k,1,l)))         zt2 = cmplx(-aimag(cu(1,k,1,l)),real(cu(1,k,1,l)))         bxy(1,k,1,l) = at2*zt1         bxy(2,k,1,l) = zero         bxy(3,k,1,l) = -at2*zt2         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero         bxy(3,k1,1,l) = zero         wp = wp + at1*(cu(1,k,1,l)*conjg(cu(1,k,1,l)) + cu(2,k,1,l)*con     1jg(cu(2,k,1,l)) + cu(3,k,1,l)*conjg(cu(3,k,1,l)))   30    continue         k1 = nyh + 1         bxy(1,1,1,l) = zero         bxy(2,1,1,l) = zero         bxy(3,1,1,l) = zero         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero         bxy(3,k1,1,l) = zero      endif   40 continue   50 continue      wm = float(nx*ny)*wp      return      endc-----------------------------------------------------------------------      subroutine PMAXWEL2(exy,bxy,cu,ffc,affp,ci,dt,wf,wm,nx,ny,kstrt,ny     1v,kxp,jblok,nyhd)c this subroutine solves 2d maxwell's equation in fourier space forc transverse electric and magnetic fields with periodic boundaryc conditions.c input: all, output: wf, wm, exy, bxyc approximate flop count is: 286*nxc*nyc + 84*(nxc + nyc)c where nxc = (nx/2-1)/nvp, nyc = ny/2 - 1, and nvp = number of procsc the magnetic field is first updated half a step using the equations:c bx(kx,ky) = bx(kx,ky) - .5*dt*sqrt(-1)*ky*ez(kx,ky)c by(kx,ky) = by(kx,ky) + .5*dt*sqrt(-1)*kx*ez(kx,ky)c bz(kx,ky) = bz(kx,ky) - .5*dt*sqrt(-1)*(kx*ey(kx,ky)-ky*ex(kx,ky))c the electric field is then updated a whole step using the equations:c ex(kx,ky) = ex(kx,ky) + c2*dt*sqrt(-1)*ky*bz(kx,ky)c                       - affp*dt*cux(kx,ky)*s(kx,ky)c ey(kx,ky) = ey(kx,ky) - c2*dt*sqrt(-1)*kx*bz(kx,ky)c                       - affp*dt*cuy(kx,ky)*s(kx,ky)c ez(kx,ky) = ez(kx,ky) + c2*dt*sqrt(-1)*(kx*by(kx,ky)-ky*bx(kx,ky))c                       - affp*dt*cuz(kx,ky)*s(kx,ky)c the magnetic field is finally updated the remaining half step withc the new electric field and the previous magnetic field equations.c where kx = 2pi*j/nx, ky = 2pi*k/ny, c2 = 1./(ci*ci)c and s(kx,ky) = exp(-((kx*ax)**2+(ky*ay)**2)c j,k = fourier mode numbers, except forc ex(kx=pi) = ey(kx=pi) = ez(kx=pi) = 0,c ex(ky=pi) = ey(ky=pi) = ez(ky=pi) = 0,c ex(kx=0,ky=0) = ey(kx=0,ky=0) = ez(kx=0,ky=0) = 0.c and similarly for bx, by, bz.c cu(i,k,j,l) = i-th component of complex current density andc exy(i,k,j,l) = i-th component of complex electric field,c bxy(i,k,j,l) = i-th component of complex magnetic field,c for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c aimag(ffc(k,j,l)) = finite-size particle shape factor sc s(kx,ky) = exp(-((kx*ax)**2+(ky*ay)**2)c for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c affp = normalization constant = nx*ny/np, where np=number of particlesc ci = reciprical of velocity of lightc dt = time interval between successive calculationsc transverse electric field energy is also calculated, usingc wf = nx*ny*nz**sum((1/affp)*|exyz(kx,ky,kz)|**2)c magnetic field energy is also calculated, usingc wm = nx*ny*nz**sum((c2/affp)*|bxyz(kx,ky,kz)|**2)c nx/ny = system length in x/y directionc jblok = number of data blocksc kxp = number of data values per blockc kstrt = starting data block numberc nyv = first dimension of field arrays, must be >= nyc nyhd = first dimension of form factor array, must be >= nyh      double precision wp, ws      complex exy, bxy, cu, ffc      complex zero, zt1, zt2, zt3, zt4, zt5, zt6, zt7, zt8, zt9      dimension exy(3,nyv,kxp,jblok), bxy(3,nyv,kxp,jblok)      dimension cu(3,nyv,kxp,jblok)      dimension ffc(nyhd,kxp,jblok)      if (ci.le.0.) return      nxh = nx/2      nyh = ny/2      ny2 = ny + 2      ks = kstrt - 2      dnx = 6.28318530717959/float(nx)      dny = 6.28318530717959/float(ny)      dth = .5*dt      c2 = 1./(ci*ci)      cdt = c2*dt      adt = affp*dt      zero = cmplx(0.,0.)      anorm = 1.0/affpc update electromagnetic field and sum field energies      ws = 0.0d0      wp = 0.0d0      if (kstrt.gt.nxh) go to 50c calculate the electromagnetic fields      do 40 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 20 j = 1, kxp      dkx = dnx*float(j + joff)      if ((j+joff).gt.0) then         do 10 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         afdt = adt*aimag(ffc(k,j,l))c update magnetic field half time step, ky > 0         zt1 = cmplx(-aimag(exy(3,k,j,l)),real(exy(3,k,j,l)))         zt2 = cmplx(-aimag(exy(2,k,j,l)),real(exy(2,k,j,l)))         zt3 = cmplx(-aimag(exy(1,k,j,l)),real(exy(1,k,j,l)))         zt4 = bxy(1,k,j,l) - dth*(dky*zt1)         zt5 = bxy(2,k,j,l) + dth*(dkx*zt1)         zt6 = bxy(3,k,j,l) - dth*(dkx*zt2 - dky*zt3)c update electric field whole time step         zt1 = cmplx(-aimag(zt6),real(zt6))         zt2 = cmplx(-aimag(zt5),real(zt5))         zt3 = cmplx(-aimag(zt4),real(zt4))         zt7 = exy(1,k,j,l) + cdt*(dky*zt1) - afdt*cu(1,k,j,l)         zt8 = exy(2,k,j,l) - cdt*(dkx*zt1) - afdt*cu(2,k,j,l)         zt9 = exy(3,k,j,l) + cdt*(dkx*zt2 - dky*zt3) - afdt*cu(3,k,j,l)c update magnetic field half time step and store electric field         zt1 = cmplx(-aimag(zt9),real(zt9))         zt2 = cmplx(-aimag(zt8),real(zt8))         zt3 = cmplx(-aimag(zt7),real(zt7))         exy(1,k,j,l) = zt7         exy(2,k,j,l) = zt8         exy(3,k,j,l) = zt9         ws = ws + anorm*(zt7*conjg(zt7) + zt8*conjg(zt8) + zt9*conjg(zt     19))         zt4 = zt4 - dth*(dky*zt1)         zt5 = zt5 + dth*(dkx*zt1)         zt6 = zt6 - dth*(dkx*zt2 - dky*zt3)         bxy(1,k,j,l) = zt4         bxy(2,k,j,l) = zt5         bxy(3,k,j,l) = zt6         wp = wp + anorm*(zt4*conjg(zt4) + zt5*conjg(zt5) + zt6*conjg(zt     16))c update magnetic field half time step, ky < 0         zt1 = cmplx(-aimag(exy(3,k1,j,l)),real(exy(3,k1,j,l)))         zt2 = cmplx(-aimag(exy(2,k1,j,l)),real(exy(2,k1,j,l)))         zt3 = cmplx(-aimag(exy(1,k1,j,l)),real(exy(1,k1,j,l)))         zt4 = bxy(1,k1,j,l) + dth*(dky*zt1)         zt5 = bxy(2,k1,j,l) + dth*(dkx*zt1)         zt6 = bxy(3,k1,j,l) - dth*(dkx*zt2 + dky*zt3)c update electric field whole time step         zt1 = cmplx(-aimag(zt6),real(zt6))         zt2 = cmplx(-aimag(zt5),real(zt5))         zt3 = cmplx(-aimag(zt4),real(zt4))         zt7 = exy(1,k1,j,l) - cdt*(dky*zt1) - afdt*cu(1,k1,j,l)         zt8 = exy(2,k1,j,l) - cdt*(dkx*zt1) - afdt*cu(2,k1,j,l)         zt9 = exy(3,k1,j,l) + cdt*(dkx*zt2 + dky*zt3) - afdt*cu(3,k1,j,     1l)c update magnetic field half time step and store electric field         zt1 = cmplx(-aimag(zt9),real(zt9))         zt2 = cmplx(-aimag(zt8),real(zt8))         zt3 = cmplx(-aimag(zt7),real(zt7))         exy(1,k1,j,l) = zt7         exy(2,k1,j,l) = zt8         exy(3,k1,j,l) = zt9         ws = ws + anorm*(zt7*conjg(zt7) + zt8*conjg(zt8) + zt9*conjg(zt     19))         zt4 = zt4 + dth*(dky*zt1)         zt5 = zt5 + dth*(dkx*zt1)         zt6 = zt6 - dth*(dkx*zt2 + dky*zt3)         bxy(1,k1,j,l) = zt4         bxy(2,k1,j,l) = zt5         bxy(3,k1,j,l) = zt6         wp = wp + anorm*(zt4*conjg(zt4) + zt5*conjg(zt5) + zt6*conjg(zt     16))   10    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         afdt = adt*aimag(ffc(1,j,l))c update magnetic field half time step         zt1 = cmplx(-aimag(exy(3,1,j,l)),real(exy(3,1,j,l)))         zt2 = cmplx(-aimag(exy(2,1,j,l)),real(exy(2,1,j,l)))         zt5 = bxy(2,1,j,l) + dth*(dkx*zt1)         zt6 = bxy(3,1,j,l) - dth*(dkx*zt2)c update electric field whole time step         zt1 = cmplx(-aimag(zt6),real(zt6))         zt2 = cmplx(-aimag(zt5),real(zt5))         zt8 = exy(2,1,j,l) - cdt*(dkx*zt1) - afdt*cu(2,1,j,l)         zt9 = exy(3,1,j,l) + cdt*(dkx*zt2) - afdt*cu(3,1,j,l)c update magnetic field half time step and store electric field         zt1 = cmplx(-aimag(zt9),real(zt9))         zt2 = cmplx(-aimag(zt8),real(zt8))         exy(1,1,j,l) = zero         exy(2,1,j,l) = zt8         exy(3,1,j,l) = zt9         ws = ws + anorm*(zt8*conjg(zt8) + zt9*conjg(zt9))         zt5 = zt5 + dth*(dkx*zt1)         zt6 = zt6 - dth*(dkx*zt2)         bxy(1,1,j,l) = zero         bxy(2,1,j,l) = zt5         bxy(3,1,j,l) = zt6         wp = wp + anorm*(zt5*conjg(zt5) + zt6*conjg(zt6))         bxy(1,k1,j,l) = zero         bxy(2,k1,j,l) = zero         bxy(3,k1,j,l) = zero         exy(1,k1,j,l) = zero         exy(2,k1,j,l) = zero         exy(3,k1,j,l) = zero      endif   20 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 30 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         afdt = adt*aimag(ffc(k,1,l))c update magnetic field half time step         zt1 = cmplx(-aimag(exy(3,k,1,l)),real(exy(3,k,1,l)))         zt3 = cmplx(-aimag(exy(1,k,1,l)),real(exy(1,k,1,l)))         zt4 = bxy(1,k,1,l) - dth*(dky*zt1)         zt6 = bxy(3,k,1,l) + dth*(dky*zt3)c update electric field whole time step         zt1 = cmplx(-aimag(zt6),real(zt6))         zt3 = cmplx(-aimag(zt4),real(zt4))         zt7 = exy(1,k,1,l) + cdt*(dky*zt1) - afdt*cu(1,k,1,l)         zt9 = exy(3,k,1,l) - cdt*(dky*zt3) - afdt*cu(3,k,1,l)c update magnetic field half time step and store electric field         zt1 = cmplx(-aimag(zt9),real(zt9))         zt3 = cmplx(-aimag(zt7),real(zt7))         exy(1,k,1,l) = zt7         exy(2,k,1,l) = zero         exy(3,k,1,l) = zt9         ws = ws + anorm*(zt7*conjg(zt7) + zt9*conjg(zt9))         zt4 = zt4 - dth*(dky*zt1)         zt6 = zt6 + dth*(dky*zt3)         bxy(1,k,1,l) = zt4         bxy(2,k,1,l) = zero         bxy(3,k,1,l) = zt6         wp = wp + anorm*(zt4*conjg(zt4) + zt6*conjg(zt6))         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero         bxy(3,k1,1,l) = zero         exy(1,k1,1,l) = zero         exy(2,k1,1,l) = zero         exy(3,k1,1,l) = zero   30    continue         k1 = nyh + 1         bxy(1,1,1,l) = zero         bxy(2,1,1,l) = zero         bxy(3,1,1,l) = zero         exy(1,1,1,l) = zero         exy(2,1,1,l) = zero         exy(3,1,1,l) = zero         bxy(1,k1,1,l) = zero         bxy(2,k1,1,l) = zero         bxy(3,k1,1,l) = zero         exy(1,k1,1,l) = zero         exy(2,k1,1,l) = zero         exy(3,k1,1,l) = zero      endif   40 continue   50 continue      wf = float(nx*ny)*ws      wm = float(nx*ny)*c2*wp      return      endc-----------------------------------------------------------------------      subroutine PEMFIELD2(fxy,exy,ffc,isign,nx,ny,kstrt,nyv,kxp,jblok,n     1yhd)c this subroutine either adds complex vector fields if isign > 0c or copies complex vector fields if isign < 0c includes additional smoothing      implicit none      integer isign, nx, ny, kstrt, nyv, kxp, jblok, nyhd      complex fxy, exy, ffc      dimension fxy(3,nyv,kxp,jblok), exy(3,nyv,kxp,jblok)      dimension ffc(nyhd,kxp,jblok)      integer i, j, k, l, nxh, nyh, ny2, k1      real at1      nxh = nx/2      nyh = ny/2      ny2 = ny + 2      if (kstrt.gt.nxh) returnc add the fields      if (isign.gt.0) then         do 50 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2         do 40 j = 1, kxp         do 20 k = 2, nyh         k1 = ny2 - k         at1 = aimag(ffc(k,j,l))         do 10 i = 1, 3         fxy(i,k,j,l) = fxy(i,k,j,l) + exy(i,k,j,l)*at1         fxy(i,k1,j,l) = fxy(i,k1,j,l) + exy(i,k1,j,l)*at1   10    continue   20    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at1 = aimag(ffc(1,j,l))         do 30 i = 1, 3         fxy(i,1,j,l) = fxy(i,1,j,l) + exy(i,1,j,l)*at1         fxy(i,k1,j,l) = fxy(i,k1,j,l) + exy(i,k1,j,l)*at1   30    continue   40    continue   50    continuec copy the fields      else if (isign.lt.0) then         do 100 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2         do 90 j = 1, kxp         do 70 k = 2, nyh         k1 = ny2 - k         at1 = aimag(ffc(k,j,l))         do 60 i = 1, 3         fxy(i,k,j,l) = exy(i,k,j,l)*at1         fxy(i,k1,j,l) = exy(i,k1,j,l)*at1   60    continue   70    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at1 = aimag(ffc(1,j,l))         do 80 i = 1, 3         fxy(i,1,j,l) = exy(i,1,j,l)*at1         fxy(i,k1,j,l) = exy(i,k1,j,l)*at1   80    continue   90    continue  100    continue      endif      return      endc-----------------------------------------------------------------------      subroutine PAVPOT23(bxy,axy,nx,ny,kstrt,nyv,kxp,jblok)c this subroutine calculates 2-1/2d vector potential from magnetic fieldc in fourier space with periodic boundary conditionsc for distributed data.c input: bxy,nx,ny,kstrt,nyv,kxp,jblok, output: axyc approximate flop count is: 38*nxc*nyc + 10*(nxc + nyc)c and nxc*nyc dividesc where nxc = (nx/2-1)/nvp, nyc = ny/2 - 1, and nvp = number of procsc the vector potential is calculated using the equations:c ax(kx,ky) = sqrt(-1)*(ky*bz(kx,ky))/(kx*kx+ky*ky)c ay(kx,ky) = -sqrt(-1)*(kx*bz(kx,ky))/(kx*kx+ky*ky)c az(kx,ky) = sqrt(-1)*(kx*by(kx,ky)-ky*bx(kx,ky))/(kx*kx+ky*ky),c where kx = 2pi*j/nx, ky = 2pi*k/ny, and j,k = fourier mode numbers,c ax(kx=pi) = ay(kx=pi) = az(kx=pi) = 0,c ax(ky=pi) = ay(ky=pi) = az(ky=pi) = 0,c ax(kx=0,ky=0) = ay(kx=0,ky=0) = az(kx=0,ky=0) = 0.c bxy(i,k,j,l) = i-th component of complex magnetic field,c axy(i,k,j,l) = i-th component of complex vector potential,c for fourier mode (jj-1,k-1), where jj = j + kxp*(l - 1)c jblok = number of data blocksc kxp = number of data values per blockc kstrt = starting data block numberc nx/ny = system length in x/y directionc nyv = first dimension of field arrays, must be >= ny      complex bxy, axy, zero, zt1, zt2, zt3      dimension bxy(3,nyv,kxp,jblok), axy(3,nyv,kxp,jblok)      nxh = nx/2      nyh = ny/2      ny2 = ny + 2      ks = kstrt - 2      dnx = 6.28318530717959/float(nx)      dny = 6.28318530717959/float(ny)      zero = cmplx(0.,0.)c calculate vector potential      if (kstrt.gt.nxh) go to 50      do 40 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks) - 1      do 20 j = 1, kxp      dkx = dnx*float(j + joff)      dkx2 = dkx*dkx      if ((j+joff).gt.0) then         do 10 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at1 = 1./(dky*dky + dkx2)         at2 = dky*at1         at3 = dkx*at1         zt1 = cmplx(-aimag(bxy(3,k,j,l)),real(bxy(3,k,j,l)))         zt2 = cmplx(-aimag(bxy(2,k,j,l)),real(bxy(2,k,j,l)))         zt3 = cmplx(-aimag(bxy(1,k,j,l)),real(bxy(1,k,j,l)))         axy(1,k,j,l) = at2*zt1         axy(2,k,j,l) = -at3*zt1         axy(3,k,j,l) = at3*zt2 - at2*zt3         zt1 = cmplx(-aimag(bxy(3,k1,j,l)),real(bxy(3,k1,j,l)))         zt2 = cmplx(-aimag(bxy(2,k1,j,l)),real(bxy(2,k1,j,l)))         zt3 = cmplx(-aimag(bxy(1,k1,j,l)),real(bxy(1,k1,j,l)))         axy(1,k1,j,l) = -at2*zt1         axy(2,k1,j,l) = -at3*zt1         axy(3,k1,j,l) = at3*zt2 + at2*zt3   10    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         at2 = 1.0/dkx         zt1 = cmplx(-aimag(bxy(3,1,j,l)),real(bxy(3,1,j,l)))         zt2 = cmplx(-aimag(bxy(2,1,j,l)),real(bxy(2,1,j,l)))         axy(1,1,j,l) = zero         axy(2,1,j,l) = -at2*zt1         axy(3,1,j,l) = at2*zt2         axy(1,k1,j,l) = zero         axy(2,k1,j,l) = zero         axy(3,k1,j,l) = zero      endif   20 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 30 k = 2, nyh         k1 = ny2 - k         dky = dny*float(k - 1)         at2 = 1.0/dky         zt1 = cmplx(-aimag(bxy(3,k,1,l)),real(bxy(3,k,1,l)))         zt2 = cmplx(-aimag(bxy(1,k,1,l)),real(bxy(1,k,1,l)))         axy(1,k,1,l) = at2*zt1         axy(2,k,1,l) = zero         axy(3,k,1,l) = -at2*zt2         axy(1,k1,1,l) = zero         axy(2,k1,1,l) = zero         axy(3,k1,1,l) = zero   30    continue         k1 = nyh + 1         axy(1,1,1,l) = zero         axy(2,1,1,l) = zero         axy(3,1,1,l) = zero         axy(1,k1,1,l) = zero         axy(2,k1,1,l) = zero         axy(3,k1,1,l) = zero      endif   40 continue   50 continue      return      endc-----------------------------------------------------------------------      subroutine PGTMODES2(pot,pott,nx,ny,it,modesx,modesy,kstrt,nyv,kxp     1,jblok,nt,modesxpd,modesyd)c this subroutine extracts lowest order modes from complex array potc and stores them into a location in a time history array pottc nx/ny = system length in x/y directionc it = current timec modesx/modesy = number of modes to store in x/y direction,c where modesx <= nx/2, modesy <= ny/2c kstrt = starting data block numberc nyv = first dimension of input array pot, nyv >= nyc kxp = number of data values per blockc jblok = number of data blocksc nt = first dimension of output array pott, nt >= itc modesyd = second dimension of array pott, modesyd  = 2*modesy-1c modesxpd = third dimension of array pott, modesxpd >= min(modesx,kxp)      complex pot, pott      dimension pot(nyv,kxp,jblok), pott(nt,modesyd,modesxpd,jblok)      if (it.gt.nt) return      nxh = nx/2      if ((modesx.gt.nxh).or.(modesy.gt.(ny/2))) return      ny2 = ny + 2      ks = kstrt - 2      if (kstrt.gt.nxh) go to 50      do 40 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks)      modesxp = modesx - joff      if (modesxp.gt.kxp) then         modesxp = kxp      else if (modesxp.le.0) then         modesxp = 0      endif      do 20 j = 1, modesxp      if ((j+joff).gt.1) then         do 10 k = 2, modesy         k1 = ny2 - k         pott(it,2*k-2,j,l) = pot(k,j,l)         pott(it,2*k-1,j,l) = pot(k1,j,l)   10    continuec mode numbers ky = 0, ny/2         pott(it,1,j,l) = pot(1,j,l)      endif   20 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 30 k = 2, modesy         pott(it,2*k-2,1,l) = pot(k,1,l)         pott(it,2*k-1,1,l) = conjg(pot(k,1,l))   30    continue         pott(it,1,1,l) = cmplx(real(pot(1,1,l)),0.0)      endif   40 continue   50 continue      return      endc-----------------------------------------------------------------------      subroutine PPTMODES2(pot,pott,nx,ny,it,modesx,modesy,kstrt,nyv,kxp     1,jblok,nt,modesxpd,modesyd)c this subroutine extracts lowest order modes from a location in a timec history array pott and stores them into complex array potc nx/ny = system length in x/y directionc it = current timec modesx/modesy = number of modes to store in x/y direction,c where modesx <= nx/2, modesy <= ny/2c kstrt = starting data block numberc nyv = first dimension of input array pot, nyv >= nyc kxp = number of data values per blockc jblok = number of data blocksc nt = first dimension of output array pott, nt >= itc modesyd = second dimension of array pott, modesyd  = 2*modesy-1c modesxpd = third dimension of array pott, modesxpd >= min(modesx,kxp)      complex pot, pott, zero      dimension pot(nyv,kxp,jblok), pott(nt,modesyd,modesxpd,jblok)      if (it.gt.nt) return      nxh = nx/2      nyh = ny/2      if ((modesx.gt.nxh).or.(modesy.gt.nyh)) return      ny2 = ny + 2      ks = kstrt - 2      zero = cmplx(0.,0.)      if (kstrt.gt.nxh) go to 100      do 90 l = 1, jblokc mode numbers 0 < kx < nx/2 and 0 < ky < ny/2      joff = kxp*(l + ks)      modesxp = modesx - joff      if (modesxp.gt.kxp) then         modesxp = kxp      else if (modesxp.le.0) then         modesxp = 0      endif      do 30 j = 1, modesxp      if ((j+joff).gt.1) then         do 10 k = 2, modesy         k1 = ny2 - k         pot(k,j,l) = pott(it,2*k-2,j,l)         pot(k1,j,l) = pott(it,2*k-1,j,l)   10    continue         do 20 k = modesy+1, nyh         k1 = ny2 - k         pot(k,j,l) = zero         pot(k1,j,l) = zero   20    continuec mode numbers ky = 0, ny/2         k1 = nyh + 1         pot(1,j,l) = pott(it,1,j,l)         pot(k1,j,l) = zero      endif   30 continue      do 60 j = modesxp+1, kxp      if ((j+joff).gt.1) then         do 40 k = 2, nyh         k1 = ny2 - k         pot(k,j,l) = zero         pot(k1,j,l) = zero   40    continue         k1 = nyh + 1c mode numbers ky = 0, ny/2         pot(1,j,l) = zero         pot(k1,j,l) = zero      endif   60 continuec mode numbers kx = 0, nx/2      if ((l+ks).eq.0) then         do 70 k = 2, modesy         k1 = ny2 - k         pot(k,1,l) = pott(it,2*k-2,1,l)         pot(k1,1,l) = zero   70    continue         do 80 k = modesy+1, nyh         k1 = ny2 - k         pot(k,1,l) = zero         pot(k1,1,l) = zero   80    continue         k1 = nyh + 1         pot(1,1,l) = cmplx(real(pott(it,1,1,l)),0.)         pot(k1,1,l) = zero      endif   90 continue  100 continue      return      endc-----------------------------------------------------------------------      subroutine PFFT2R(f,g,bs,br,isign,ntpose,mixup,sct,indx,indy,kstrt     1,nxvh,nyv,kxp,kyp,kypd,jblok,kblok,nxhyd,nxyhd)c this subroutine performs a two dimensional real to complex fastc fourier transform and its inverse, using complex arithmetic,c for data which is distributed in blocksc for isign = 0, input: isign, indx, indy, kstrt, nxhyd, nxyhdc output: mixup, sctc for isign = (-1,1), input: all, output: f, g, bs, brc for isign = -1, approximate flop count: N*(5*log2(N) + 10)/nvpc for isign = 1,  approximate flop count: N*(5*log2(N) + 8)/nvpc where N = (nx/2)*ny, and nvp = number of procsc indx/indy = exponent which determines length in x/y direction,c where nx=2**indx, ny=2**indyc ntpose = (0,1) = (no,yes) input, output data are transposedc if isign = 0, the fft tables are preparedc if isign = -1, an inverse fourier transform is performedc if ntpose = 0, f is the input and output array, g is a scratch arrayc f(n,m,l) = (1/nx*ny)*sum(f(j,k,i)*c       exp(-sqrt(-1)*2pi*n*j/nx)*exp(-sqrt(-1)*2pi*mm*kk/ny))c where mm = m + kyp*(l - 1) and kk = k + kyp*(i - 1)c if ntpose = 1, f is the input and g is the outputc g(m,n,l) = (1/nx*ny)*sum(f(j,k,i)*c       exp(-sqrt(-1)*2pi*nn*j/nx)*exp(-sqrt(-1)*2pi*m*kk/ny))c where nn = n + kxp*(l - 1) and kk = k + kyp*(i - 1)c if isign = 1, a forward fourier transform is performedc if ntpose = 0, f is the input and output array, g is a scratch arrayc f(j,k,i) = sum(f(n,m,l)*exp(sqrt(-1)*2pi*n*j/nx)*c       exp(sqrt(-1)*2pi*mm*kk/ny))c where mm = m + kyp*(l - 1) and kk = k + kyp*(i - 1)c if ntpose = 1, g is the input and f is the outputc f(j,k,i) = sum(g(m,n,l)*exp(sqrt(-1)*2pi*nn*j/nx)*c       exp(sqrt(-1)*2pi*m*kk/ny))c where nn = n + kxp*(l - 1) and kk = k + kyp*(i - 1)c bs, br = scratch arraysc kstrt = starting data block numberc nxvh/nyv = first dimension of f/gc kypd = second dimension of fc kxp/kyp = number of data values per block in x/yc jblok/kblok = number of data blocks in x/yc mixup = array of bit reversed addressesc sct = sine/cosine tablec nxhyd = maximum of (nx/2,ny)c nxyhd = one half of maximum of (nx,ny)c the real data is stored in a complex array of length nx/2, nyc with the odd/even x points stored in the real/imaginary parts.c in complex notation, fourier coefficients are stored as follows:c if ntpose = 0,c f(j,k,i) = mode j-1,kk-1, where kk = k + kyp*(i - 1)c 1 <= j <= nx/2 and 1 <= kk <= ny, except forc f(1,k,i) = mode nx/2,kk-1, where ny/2+2 <= kk <= ny, andc imaginary part of f(1,1,1) = real part of mode nx/2,0 andc imaginary part of f(1,1,(ny/2)/kyp+1) = real part of mode nx/2,ny/2c if ntpose = 1,c g(k,j,i) = mode jj-1,k-1, where jj = j + kxp*(i - 1)c 1 <= jj <= nx/2 and 1 <= k <= ny, except forc g(k,1,1) = mode nx/2,k-1, where ny/2+2 <= k <= ny, andc imaginary part of g(1,1,1) = real part of mode nx/2,0 andc imaginary part of g(ny/2+1,1,1) = real part of mode nx/2,ny/2c written by viktor k. decyk, uclac parallel version      implicit none      integer isign, ntpose, indx, indy, kstrt, nxvh, nyv, kxp, kyp      integer kypd, jblok, kblok, nxhyd, nxyhd, mixup      complex f, g, bs, br, sct      dimension f(nxvh,kypd,kblok), g(nyv,kxp,jblok)      dimension bs(kxp,kyp,kblok), br(kxp,kyp,jblok)      dimension mixup(nxhyd), sct(nxyhd)c local data      integer indx1, indx1y, nx, nxh, nxhh, nxh2, ny, nyh, ny2, nxy      integer nxhy, ks, j, k, lb, ll, jb, it, nxyh, nrx, nry, l, i, m      integer ns, ns2, km, kmr, k1, k2, j1, j2      real dnxy, arg, ani      complex s, t, t1      indx1 = indx - 1      indx1y = max0(indx1,indy)      nx = 2**indx      nxh = nx/2      nxhh = nx/4      nxh2 = nxh + 2      ny = 2**indy      nyh = ny/2      ny2 = ny + 2      nxy = max0(nx,ny)      nxhy = 2**indx1y      ks = kstrt - 2      if (isign) 50, 10, 300c prepare fft tablesc bit-reverse index table: mixup(j) = 1 + reversed bits of (j - 1)   10 do 30 j = 1, nxhy      lb = j - 1      ll = 0      do 20 k = 1, indx1y      jb = lb/2      it = lb - 2*jb      lb = jb      ll = 2*ll + it   20 continue      mixup(j) = ll + 1   30 continuec sine/cosine table for the angles 2*n*pi/nxy      nxyh = nxy/2      dnxy = 6.28318530717959/float(nxy)      do 40 j = 1, nxyh      arg = dnxy*float(j - 1)      sct(j) = cmplx(cos(arg),-sin(arg))   40 continue      returnc inverse fourier transform   50 if (kstrt.gt.ny) go to 180      nrx = nxhy/nxh      do 80 l = 1, kblokc bit-reverse array elements in x      do 70 j = 1, nxh      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 70      do 60 k = 1, kyp      t = f(j1,k,l)      f(j1,k,l) = f(j,k,l)      f(j,k,l) = t   60 continue   70 continue   80 continuec first transform in x      nrx = nxy/nxh      do 130 m = 1, indx1      ns = 2**(m - 1)      ns2 = ns + ns      km = nxhh/ns      kmr = km*nrx      do 120 l = 1, kblok      do 110 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 100 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 90 i = 1, kyp      t = s*f(j2,i,l)      f(j2,i,l) = f(j1,i,l) - t      f(j1,i,l) = f(j1,i,l) + t   90 continue  100 continue  110 continue  120 continue  130 continuec unscramble coefficients and normalize      kmr = nxy/nx      ani = 1./float(2*nx*ny)      do 170 l = 1, kblok      do 150 j = 2, nxhh      t1 = cmplx(aimag(sct(1+kmr*(j-1))),-real(sct(1+kmr*(j-1))))      do 140 k = 1, kyp      t = conjg(f(nxh2-j,k,l))      s = f(j,k,l) + t      t = (f(j,k,l) - t)*t1      f(j,k,l) = ani*(s + t)      f(nxh2-j,k,l) = ani*conjg(s - t)  140 continue  150 continue      do 160 k = 1, kyp      f(nxhh+1,k,l) = 2.*ani*conjg(f(nxhh+1,k,l))      f(1,k,l) = 2.*ani*cmplx(real(f(1,k,l)) + aimag(f(1,k,l)),real(f(1,     1k,l)) - aimag(f(1,k,l)))  160 continue  170 continuec transpose f array to g  180 call PTPOSE(f,g,bs,br,nxh,ny,kstrt,nxvh,nyv,kxp,kyp,kxp,kypd,jblok     1,kblok)      if (kstrt.gt.nxh) go to 290      nry = nxhy/ny      do 210 l = 1, jblokc bit-reverse array elements in y      do 200 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 200      do 190 j = 1, kxp      t = g(k1,j,l)      g(k1,j,l) = g(k,j,l)      g(k,j,l) = t  190 continue  200 continue  210 continuec then transform in y      nry = nxy/ny      do 260 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 250 l = 1, jblok      do 240 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 230 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 220 i = 1, kxp      t = s*g(j2,i,l)      g(j2,i,l) = g(j1,i,l) - t      g(j1,i,l) = g(j1,i,l) + t  220 continue  230 continue  240 continue  250 continue  260 continuec unscramble modes kx = 0, nx/2      do 280 l = 1, jblok      if ((l+ks).gt.0) go to 280      do 270 k = 2, nyh      s = g(ny2-k,1,l)      g(ny2-k,1,l) = .5*cmplx(aimag(g(k,1,l) + s),real(g(k,1,l) - s))      g(k,1,l) = .5*cmplx(real(g(k,1,l) + s),aimag(g(k,1,l) - s))  270 continue  280 continuec transpose g array to f  290 if (ntpose.eq.0) call PTPOSE(g,f,br,bs,ny,nxh,kstrt,nyv,nxvh,kyp,k     1xp,kypd,kxp,kblok,jblok)      returnc forward fourier transformc transpose f array to g  300 if (ntpose.eq.0) call PTPOSE(f,g,bs,br,nxh,ny,kstrt,nxvh,nyv,kxp,k     1yp,kxp,kypd,jblok,kblok)      if (kstrt.gt.nxh) go to 410      nry = nxhy/ny      do 350 l = 1, jblokc scramble modes kx = 0, nx/2      if ((l+ks).gt.0) go to 320      do 310 k = 2, nyh      s = cmplx(aimag(g(ny2-k,1,l)),real(g(ny2-k,1,l)))      g(ny2-k,1,l) = conjg(g(k,1,l) - s)      g(k,1,l) = g(k,1,l) + s  310 continuec bit-reverse array elements in y  320 do 340 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 340      do 330 j = 1, kxp      t = g(k1,j,l)      g(k1,j,l) = g(k,j,l)      g(k,j,l) = t  330 continue  340 continue  350 continuec first transform in y      nry = nxy/ny      do 400 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 390 l = 1, jblok      do 380 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 370 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 360 i = 1, kxp      t = s*g(j2,i,l)      g(j2,i,l) = g(j1,i,l) - t      g(j1,i,l) = g(j1,i,l) + t  360 continue  370 continue  380 continue  390 continue  400 continuec transpose g array to f  410 call PTPOSE(g,f,br,bs,ny,nxh,kstrt,nyv,nxvh,kyp,kxp,kypd,kxp,kblok     1,jblok)      if (kstrt.gt.ny) return      nrx = nxhy/nxh      kmr = nxy/nx      do 470 l = 1, kblok