ypd,kxp,kblok,jblok)      returnc forward fourier transformc transpose f array to g  360 if (ntpose.eq.0) call P3TPOSEX(f,g,nxh,ny,kstrt,nxvh,nyv,kxp,kyp,k     1xp,kypd,jblok,kblok)      if (kstrt.gt.nxh) go to 480      nry = nxhy/ny      do 420 l = 1, jblokc scramble modes kx = 0, nx/2      if ((l+ks).gt.0) go to 390      do 380 k = 2, nyh      do 370 jj = 1, 3      s = cmplx(aimag(g(jj,ny2-k,1,l)),real(g(jj,ny2-k,1,l)))      g(jj,ny2-k,1,l) = conjg(g(jj,k,1,l) - s)      g(jj,k,1,l) = g(jj,k,1,l) + s  370 continue  380 continuec bit-reverse array elements in y  390 do 410 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 410      do 400 j = 1, kxp      t1 = g(1,k1,j,l)      t2 = g(2,k1,j,l)      t3 = g(3,k1,j,l)      g(1,k1,j,l) = g(1,k,j,l)      g(2,k1,j,l) = g(2,k,j,l)      g(3,k1,j,l) = g(3,k,j,l)      g(1,k,j,l) = t1      g(2,k,j,l) = t2      g(3,k,j,l) = t3  400 continue  410 continue  420 continuec first transform in y      nry = nxy/ny      do 470 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 460 l = 1, jblok      do 450 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 440 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 430 i = 1, kxp      t1 = s*g(1,j2,i,l)      t2 = s*g(2,j2,i,l)      t3 = s*g(3,j2,i,l)      g(1,j2,i,l) = g(1,j1,i,l) - t1      g(2,j2,i,l) = g(2,j1,i,l) - t2      g(3,j2,i,l) = g(3,j1,i,l) - t3      g(1,j1,i,l) = g(1,j1,i,l) + t1      g(2,j1,i,l) = g(2,j1,i,l) + t2      g(3,j1,i,l) = g(3,j1,i,l) + t3  430 continue  440 continue  450 continue  460 continue  470 continuec transpose g array to f  480 call P3TPOSEX(g,f,ny,nxh,kstrt,nyv,nxvh,kyp,kxp,kypd,kxp,kblok,jbl     1ok)      if (kstrt.gt.ny) return      nrx = nxhy/nxh      kmr = nxy/nx      do 560 l = 1, kblokc scramble coefficients      do 510 j = 2, nxhh      t1 = cmplx(aimag(sct(1+kmr*(j-1))),real(sct(1+kmr*(j-1))))      do 500 k = 1, kyp      do 490 jj = 1, 3      t = conjg(f(jj,nxh2-j,k,l))      s = f(jj,j,k,l) + t      t = (f(jj,j,k,l) - t)*t1      f(jj,j,k,l) = s + t      f(jj,nxh2-j,k,l) = conjg(s - t)  490 continue  500 continue  510 continue      do 530 k = 1, kyp      do 520 jj = 1, 3      f(jj,nxhh+1,k,l) = 2.*conjg(f(jj,nxhh+1,k,l))      f(jj,1,k,l) = cmplx(real(f(jj,1,k,l)) + aimag(f(jj,1,k,l)),real(f(     1jj,1,k,l)) - aimag(f(jj,1,k,l)))  520 continue  530 continuec bit-reverse array elements in x      do 550 j = 1, nxh      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 550      do 540 k = 1, kyp      t1 = f(1,j1,k,l)      t2 = f(2,j1,k,l)      t3 = f(3,j1,k,l)      f(1,j1,k,l) = f(1,j,k,l)      f(2,j1,k,l) = f(2,j,k,l)      f(3,j1,k,l) = f(3,j,k,l)      f(1,j,k,l) = t1      f(2,j,k,l) = t2      f(3,j,k,l) = t3  540 continue  550 continue  560 continuec then transform in x      nrx = nxy/nxh      do 610 m = 1, indx1      ns = 2**(m - 1)      ns2 = ns + ns      km = nxhh/ns      kmr = km*nrx      do 600 l = 1, kblok      do 590 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 580 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 570 i = 1, kyp      t1 = s*f(1,j2,i,l)      t2 = s*f(2,j2,i,l)      t3 = s*f(3,j2,i,l)      f(1,j2,i,l) = f(1,j1,i,l) - t1      f(2,j2,i,l) = f(2,j1,i,l) - t2      f(3,j2,i,l) = f(3,j1,i,l) - t3      f(1,j1,i,l) = f(1,j1,i,l) + t1      f(2,j1,i,l) = f(2,j1,i,l) + t2      f(3,j1,i,l) = f(3,j1,i,l) + t3  570 continue  580 continue  590 continue  600 continue  610 continuec swap complex components      do 640 l = 1, kblok      do 630 i = 1, kyp      do 620 j = 1, nxh      at1 = real(f(3,j,i,l))      f(3,j,i,l) = cmplx(aimag(f(2,j,i,l)),aimag(f(3,j,i,l)))      at2 = real(f(2,j,i,l))      f(2,j,i,l) = cmplx(at1,aimag(f(1,j,i,l)))      f(1,j,i,l) = cmplx(real(f(1,j,i,l)),at2)  620 continue  630 continue  640 continue      return      endc-----------------------------------------------------------------------      subroutine PFFT2C(f,g,bs,br,isign,ntpose,mixup,sct,indx,indy,kstrt     1,nxv,nyv,kxp,kyp,kypd,jblok,kblok,nxyd,nxyhd)c this subroutine performs a two dimensional complex to complex fastc fourier transform and its inverse, using complex arithmetic,c for data which is distributed in blocksc for isign = 0, input: isign, indx, indy, kstrt, nxyd, nxyhdc output: mixup, sctc for isign = (-1,1), input: all, output: f, g, bs, brc approximate flop count: 5*N*log2(N)/nvpc where N = nx*ny, and nvp = number of procsc indx/indy = exponent which determines length in x/y direction,c where nx=2**indx, ny=2**indyc ntpose = (0,1) = (no,yes) input, output data are transposedc if isign = 0, the fft tables are preparedc if isign = -1, an inverse fourier transform is performedc if ntpose = 0, f is the input and output array, g is a scratch arrayc f(n,m,l) = (1/nx*ny)*sum(f(j,k,i)*c       exp(-sqrt(-1)*2pi*n*j/nx)*exp(-sqrt(-1)*2pi*mm*kk/ny))c where mm = m + kyp*(l - 1) and kk = k + kyp*(i - 1)c if ntpose = 1, f is the input and g is the outputc g(m,n,l) = (1/nx*ny)*sum(f(j,k,i)*c       exp(-sqrt(-1)*2pi*nn*j/nx)*exp(-sqrt(-1)*2pi*m*kk/ny))c where nn = n + kxp*(l - 1) and kk = k + kyp*(i - 1)c if isign = 1, a forward fourier transform is performedc if ntpose = 0, f is the input and output array, g is a scratch arrayc f(j,k,i) = sum(f(n,m,l)*exp(sqrt(-1)*2pi*n*j/nx)*c       exp(sqrt(-1)*2pi*mm*kk/ny))c where mm = m + kyp*(l - 1) and kk = k + kyp*(i - 1)c if ntpose = 1, g is the input and f is the outputc f(j,k,i) = sum(g(m,n,l)*exp(sqrt(-1)*2pi*nn*j/nx)*c       exp(sqrt(-1)*2pi*m*kk/ny))c where nn = n + kxp*(l - 1) and kk = k + kyp*(i - 1)c bs, br = scratch arraysc kstrt = starting data block numberc nxv/nyv = first dimension of f/gc kypd = second dimension of f c kxp/kyp = number of data values per block in x/yc jblok/kblok = number of data blocks in x/yc mixup = array of bit reversed addressesc sct = sine/cosine tablec nxy = maximum of (nx,ny)c nxyh = one half of maximum of (nx,ny)c written by viktor k. decyk, uclac parallel version      implicit none      integer isign, ntpose, indx, indy, kstrt, nxv, nyv, kxp, kyp      integer kypd, jblok, kblok, nxyd, nxyhd, mixup      complex f, g, bs, br, sct      dimension f(nxv,kypd,kblok), g(nyv,kxp,jblok)      dimension bs(kxp,kyp,kblok), br(kxp,kyp,jblok)      dimension mixup(nxyd), sct(nxyhd)c local data      integer indxy, nx, nxh, ny, nyh, nxy, j, k, lb, ll, jb, it, nxyh      integer nrx, nry, l, i, m, ns, ns2, km, kmr, k1, k2, j1, j2      real dnxy, arg, ani      complex s, t      indxy = max0(indx,indy)      nx = 2**indx      nxh = nx/2      ny = 2**indy      nyh = ny/2      nxy = 2**indxy      if (isign) 50, 10, 270c prepare fft tablesc bit-reverse index table: mixup(j) = 1 + reversed bits of (j - 1)   10 do 30 j = 1, nxy      lb = j - 1      ll = 0      do 20 k = 1, indxy      jb = lb/2      it = lb - 2*jb      lb = jb      ll = 2*ll + it   20 continue      mixup(j) = ll + 1   30 continuec sine/cosine table for the angles 2*n*pi/nxy      nxyh = nxy/2      dnxy = 6.28318530717959/float(nxy)      do 40 j = 1, nxyh      arg = dnxy*float(j - 1)      sct(j) = cmplx(cos(arg),-sin(arg))   40 continue      returnc inverse fourier transform   50 if (kstrt.gt.ny) go to 140      nrx = nxy/nx      do 80 l = 1, kblokc bit-reverse array elements in x      do 70 j = 1, nx      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 70      do 60 k = 1, kyp      t = f(j1,k,l)      f(j1,k,l) = f(j,k,l)      f(j,k,l) = t   60 continue   70 continue   80 continuec first transform in x      do 130 m = 1, indx      ns = 2**(m - 1)      ns2 = ns + ns      km = nxh/ns      kmr = km*nrx      do 120 l = 1, kblok      do 110 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 100 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 90 i = 1, kyp      t = s*f(j2,i,l)      f(j2,i,l) = f(j1,i,l) - t      f(j1,i,l) = f(j1,i,l) + t   90 continue  100 continue  110 continue  120 continue  130 continuec transpose f array to g  140 call PTPOSE(f,g,bs,br,nx,ny,kstrt,nxv,nyv,kxp,kyp,kxp,kypd,jblok,k     1blok)      if (kstrt.gt.nx) go to 260      nry = nxy/ny      do 170 l = 1, jblokc bit-reverse array elements in y      do 160 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 160      do 150 j = 1, kxp      t = g(k1,j,l)      g(k1,j,l) = g(k,j,l)      g(k,j,l) = t  150 continue  160 continue  170 continuec then transform in y      do 220 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 210 l = 1, jblok      do 200 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 190 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 180 i = 1, kxp      t = s*g(j2,i,l)      g(j2,i,l) = g(j1,i,l) - t      g(j1,i,l) = g(j1,i,l) + t  180 continue  190 continue  200 continue  210 continue  220 continuec normalize result      ani = 1./float(nx*ny)      do 250 l = 1, jblok      do 240 j = 1, kxp      do 230 k = 1, ny      g(k,j,l) = g(k,j,l)*ani  230 continue  240 continue  250 continuec transpose g array to f  260 if (ntpose.eq.0) call PTPOSE(g,f,br,bs,ny,nx,kstrt,nyv,nxv,kyp,kxp     1,kypd,kxp,kblok,jblok)      returnc forward fourier transformc transpose f array to g  270 if (ntpose.eq.0) call PTPOSE(f,g,bs,br,nx,ny,kstrt,nxv,nyv,kxp,kyp     1,kxp,kypd,jblok,kblok)      if (kstrt.gt.nx) go to 360      nry = nxy/ny      do 300 l = 1, jblokc bit-reverse array elements in y      do 290 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 290      do 280 j = 1, kxp      t = g(k1,j,l)      g(k1,j,l) = g(k,j,l)      g(k,j,l) = t  280 continue  290 continue  300 continuec first transform in y      do 350 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 340 l = 1, jblok      do 330 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 320 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 310 i = 1, kxp      t = s*g(j2,i,l)      g(j2,i,l) = g(j1,i,l) - t      g(j1,i,l) = g(j1,i,l) + t  310 continue  320 continue  330 continue  340 continue  350 continuec transpose g array to f  360 call PTPOSE(g,f,br,bs,ny,nx,kstrt,nyv,nxv,kyp,kxp,kypd,kxp,kblok,j     1blok)      if (kstrt.gt.ny) return      nrx = nxy/nx      do 390 l = 1, kblokc bit-reverse array elements in x      do 380 j = 1, nx      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 380      do 370 k = 1, kyp      t = f(j1,k,l)      f(j1,k,l) = f(j,k,l)      f(j,k,l) = t  370 continue  380 continue  390 continuec then transform in x      do 440 m = 1, indx      ns = 2**(m - 1)      ns2 = ns + ns      km = nxh/ns      kmr = km*nrx      do 430 l = 1, kblok      do 420 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 410 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 400 i = 1, kyp      t = s*f(j2,i,l)      f(j2,i,l) = f(j1,i,l) - t      f(j1,i,l) = f(j1,i,l) + t  400 continue  410 continue  420 continue  430 continue  440 continue      return      endc-----------------------------------------------------------------------      subroutine WPFFT2RN(f,g,bs,br,ss,isign,ntpose,mixup,sct,indx,indy,     1kstrt,nxvh,nyv,kxp,kyp,kypd,jblok,kblok,ndim,nxhyd,nxyhd)c wrapper function for real to complex fft      implicit none      integer isign, ntpose, indx, indy, kstrt, nxvh, nyv, kxp, kyp      integer kypd, jblok, kblok, ndim, nxhyd, nxyhd, mixup      complex f, g, bs, br, ss, sct      dimension f(ndim,nxvh,kypd,kblok), g(ndim,nyv,kxp,jblok)      dimension bs(ndim,kxp,kyp,kblok), br(ndim,kxp,kyp,jblok)      dimension ss(ndim,nxvh)      dimension mixup(nxhyd), sct(nxyhd)c local data      integer nxh, ny, kxpi, kypi      data kxpi, kypi /1,1/c calculate range of indices      nxh = 2**(indx - 1)      ny = 2**indyc inverse fourier transform      if (isign.lt.0) thenc perform x fft         call PFFT2RNXX(f,ss,isign,mixup,sct,indx,indy,kstrt,kypi,kyp,nx     1vh,kypd,kblok,ndim,nxhyd,nxyhd)c transpose f array to g         call PNTPOSE(f,g,bs,br,nxh,ny,kstrt,nxvh,nyv,kxp,kyp,kxp,kypd,j     1blok,kblok,ndim)c perform y fft         call PFFT2RNXY(g,isign,mixup,sct,indx,indy,kstrt,kxpi,kxp,nyv,k     1xp,jblok,ndim,nxhyd,nxyhd)c transpose g array to f         if (ntpose.eq.0) call PNTPOSE(g,f,br,bs,ny,nxh,kstrt,nyv,nxvh,k     1yp,kxp,kypd,kxp,kblok,jblok,ndim)c forward fourier transform      else if (isign.gt.0) thenc transpose f array to g         if (ntpose.eq.0) call PNTPOSE(f,g,bs,br,nxh,ny,kstrt,nxvh,nyv,k     1xp,kyp,kxp,kypd,jblok,kblok,ndim)c perform y fft         call PFFT2RNXY(g,isign,mixup,sct,indx,indy,kstrt,kxpi,kxp,nyv,k     1xp,jblok,ndim,nxhyd,nxyhd)c transpose g array to f         call PNTPOSE(g,f,br,bs,ny,nxh,kstrt,nyv,nxvh,kyp,kxp,kypd,kxp,k     1blok,jblok,ndim)c perform x fft         call PFFT2RNXX(f,ss,isign,mixup,sct,indx,indy,kstrt,kypi,kyp,nx     1vh,kypd,kblok,ndim,nxhyd,nxyhd)      endif      return      endc-----------------------------------------------------------------------      subroutine WPFFT2RXN(f,g,ss,isign,ntpose,mixup,sct,indx,indy,kstrt     1,nxvh,nyv,kxp,kyp,kypd,jblok,kblok,ndim,nxhyd,nxyhd)c wrapper function for real to complex fft      implicit none      integer isign, ntpose, mixup, indx, indy, kstrt, nxvh      integer nyv, kxp, kyp, kypd, jblok, kblok, ndim, nxhyd, nxyhd      complex f, g, ss, sct      dimension f(ndim,nxvh,kypd,kblok), g(ndim,nyv,kxp,jblok)      dimension ss(ndim,nxvh)      dimension mixup(nxhyd), sct(nxyhd)c local data      integer nxh, ny, kxpi, kypi      data kxpi, kypi /1,1/c calculate range of indices      nxh = 2**(indx - 1)      ny = 2**indyc inverse fourier transform      if (isign.lt.0) thenc perform x fft         call PFFT2RNXX(f,ss,isign,mixup,sct,indx,indy,kstrt,kypi,kyp,nx     1vh,kypd,kblok,ndim,nxhyd,nxyhd)c transpose f array to g         call PNTPOSEX(f,g,nxh,ny,kstrt,nxvh,nyv,kxp,kyp,kxp,kypd,jblok,     1kblok,ndim)c perform y fft         call PFFT2RNXY(g,isign,mixup,sct,indx,indy,kstrt,kxpi,kxp,nyv,k     1xp,jblok,ndim,nxhyd,nxyhd)c transpose g array to f         if (ntpose.eq.0) call PNTPOSEX(g,f,ny,nxh,kstrt,nyv,nxvh,kyp,kx     1p,kypd,kxp,kblok,jblok,ndim)c forward fourier transform      else if (isign.gt.0) thenc transpose f array to g         if (ntpose.eq.0) call PNTPOSEX(f,g,nxh,ny,kstrt,nxvh,nyv,kxp,ky     1p,kxp,kypd,jblok,kblok,ndim)c perform y fft         call PFFT2RNXY(g,isign,mixup,sct,indx,indy,kstrt,kxpi,kxp,nyv,k     1xp,jblok,ndim,nxhyd,nxyhd)c transpose g array to f         call PNTPOSEX(g,f,ny,nxh,kstrt,nyv,nxvh,kyp,kxp,kypd,kxp,kblok,     1jblok,ndim)c perform x fft         call PFFT2RNXX(f,ss,isign,mixup,sct,indx,indy,kstrt,kypi,kyp,nx     1vh,kypd,kblok,ndim,nxhyd,nxyhd)      endif      return      endc-----------------------------------------------------------------------      subroutine PFFT2RNXX(f,ss,isign,mixup,sct,indx,indy,kstrt,kypi,kyp     1p,nxvh,kypd,kblok,ndim,nxhyd,nxyhd)c this subroutine performs the x part of N two dimensional real toc complex fast fourier transforms and their inverses, for a subset of y,c using complex arithmetic, for data which is distributed in blocksc for isign = (-1,1), input: all, output: fc for isign = -1, approximate flop count: M*(5*log2(M) + 10)/nvpc for isign = 1,  approximate flop count: M*(5*log2(M) + 8)/nvpc where M = (nx/2)*ny, and nvp = number of procsc indx/indy = exponent which determines length in x/y direction,c where nx=2**indx, ny=2**indyc if isign = -1, an inverse fourier transform is performedc f(1:N,n,m,i) = (1/nx*ny)*sum(f(1:N,j,k,i)*exp(-sqrt(-1)*2pi*n*j/nx)c if isign = 1, a forward fourier transform is performedc f(1:N,j,k,i) = sum(f(1:N,n,m,i)*exp(sqrt(-1)*2pi*n*j/nx))c kstrt = starting data block numberc kypi = initial y index usedc kypp = number of y indices usedc nxvh = first dimension of fc kypd = second dimension of fc kblok = number of data blocks in yc ndim = leading dimension of arrays f and gc s = scratch arrayc mixup = array of bit reversed addressesc sct = sine/cosine tablec nxhyd = maximum of (nx/2,ny)c nxyhd = one half of maximum of (nx,ny)c the real data is stored in a complex array of length nx/2, nyc with the odd/even x points stored in the real/imaginary parts.c in complex notation, fourier coefficients are stored as follows:c f(j,k,i) = mode j-1,kk-1, where kk = k + kyp*(i - 1)c 1 <= j <= nx/2 and 1 <= kk <= ny, except forc f(1,k,i) = mode nx/2,kk-1, where ny/2+2 <= kk <= ny, andc imaginary part of f(1,1,1) = real part of mode nx/2,0 andc imaginary part of f(1,1,(ny/2)/kyp+1) = real part of mode nx/2,ny/2c written by viktor k. decyk, uclac parallel, RISC optimized version      implicit none      integer isign, mixup, indx, indy, kstrt, nxvh, kypi, kypp      integer kypd, kblok, ndim, nxhyd, nxyhd      complex f, ss, sct      dimension f(ndim,nxvh,kypd,kblok), ss(ndim,nxvh)      dimension mixup(nxhyd), sct(nxyhd)c local data      integer indx1, indx1y, nx, nxh, nxhh, nxh2, ny      integer nxy, nxhy, ks, kypt, j, k, nrx      integer l, i, m, ns, ns2, km, kmr, k1, k2, j1, j2, jj      real ani      complex s, t, t1      if (isign.eq.0) return      indx1 = indx - 1      indx1y = max0(indx1,indy)      nx = 2**indx      nxh = nx/2      nxhh = nx/4      nxh2 = nxh + 2      ny = 2**indy      nxy = max0(nx,ny)      nxhy = 2**indx1y      ks = kstrt - 2      kypt = kypi + kypp - 1      if (kstrt.gt.ny) return      if (isign.gt.0) go to 170c inverse fourier transform      ani = 1./float(2*nx*ny)c swap complex components      call PSWAPC2N(f,ss,isign,nxh,kypi,kypt,nxvh,kypd,kblok,ndim)      nrx = nxhy/nxh      do 40 l = 1, kblokc bit-reverse array elements in x      do 30 j = 1, nxh      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 30      do 20 k = kypi, kypt      do 10 jj = 1, ndim      t1 = f(jj,j1,k,l)      f(jj,j1,k,l) = f(jj,j,k,l)      f(jj,j,k,l) = t1   10 continue   20 continue   30 continue   40 continuec first transform in x      nrx = nxy/nxh      do 100 m = 1, indx1      ns = 2**(m - 1)      ns2 = ns + ns      km = nxhh/ns      kmr = km*nrx      do 90 l = 1, kblok      do 80 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 70 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 60 i = kypi, kypt      do 50 jj = 1, ndim      t1 = s*f(jj,j2,i,l)      f(jj,j2,i,l) = f(jj,j1,i,l) - t1      f(jj,j1,i,l) = f(jj,j1,i,l) + t1   50 continue   60 continue   70 continue   80 continue   90 continue  100 continuec unscramble coefficients and normalize      kmr = nxy/nx      do 160 l = 1, kblok      do 130 j = 2, nxhh      t1 = cmplx(aimag(sct(1+kmr*(j-1))),-real(sct(1+kmr*(j-1))))      do 120 k = kypi, kypt      do 110 jj = 1, ndim      t = conjg(f(jj,nxh2-j,k,l))      s = f(jj,j,k,l) + t      t = (f(jj,j,k,l) - t)*t1      f(jj,j,k,l) = ani*(s + t)      f(jj,nxh2-j,k,l) = ani*conjg(s - t)  110 continue  120 continue  130 continue      do 150 k = kypi, kypt      do 140 jj = 1, ndim      f(jj,nxhh+1,k,l) = 2.*ani*conjg(f(jj,nxhh+1,k,l))      f(jj,1,k,l) = 2.*ani*cmplx(real(f(jj,1,k,l)) + aimag(f(jj,1,k,l)),     1real(f(jj,1,k,l)) - aimag(f(jj,1,k,l)))  140 continue  150 continue  160 continue      returnc forward fourier transform  170 kmr = nxy/nx      do 260 l = 1, kblokc scramble coefficients      do 200 j = 2, nxhh      t1 = cmplx(aimag(sct(1+kmr*(j-1))),real(sct(1+kmr*(j-1))))      do 190 k = kypi, kypt      do 180 jj = 1, ndim      t = conjg(f(jj,nxh2-j,k,l))      s = f(jj,j,k,l) + t      t = (f(jj,j,k,l) - t)*t1      f(jj,j,k,l) = s + t      f(jj,nxh2-j,k,l) = conjg(s - t)  180 continue  190 continue  200 continue      do 220 k = kypi, kypt      do 210 jj = 1, ndim      f(jj,nxhh+1,k,l) = 2.*conjg(f(jj,nxhh+1,k,l))      f(jj,1,k,l) = cmplx(real(f(jj,1,k,l)) + aimag(f(jj,1,k,l)),real(f(     1jj,1,k,l)) - aimag(f(jj,1,k,l)))  210 continue  220 continue      nrx = nxhy/nxhc bit-reverse array elements in x      do 250 j = 1, nxh      j1 = (mixup(j) - 1)/nrx + 1      if (j.ge.j1) go to 250      do 240 k = kypi, kypt      do 230 jj = 1, ndim      t1 = f(jj,j1,k,l)      f(jj,j1,k,l) = f(jj,j,k,l)      f(jj,j,k,l) = t1  230 continue  240 continue  250 continue  260 continuec then transform in x      nrx = nxy/nxh      do 320 m = 1, indx1      ns = 2**(m - 1)      ns2 = ns + ns      km = nxhh/ns      kmr = km*nrx      do 310 l = 1, kblok      do 300 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 290 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 280 i = kypi, kypt      do 270 jj = 1, ndim      t1 = s*f(jj,j2,i,l)      f(jj,j2,i,l) = f(jj,j1,i,l) - t1      f(jj,j1,i,l) = f(jj,j1,i,l) + t1  270 continue  280 continue  290 continue  300 continue  310 continue  320 continuec swap complex components      call PSWAPC2N(f,ss,isign,nxh,kypi,kypt,nxvh,kypd,kblok,ndim)      return      endc-----------------------------------------------------------------------      subroutine PFFT2RNXY(g,isign,mixup,sct,indx,indy,kstrt,kxpi,kxpp,n     1yv,kxp,jblok,ndim,nxhyd,nxyhd)c this subroutine performs the y part of N two dimensional real toc complex fast fourier transforms and their inverses, for a subset of x,c using complex arithmetic, for data which is distributed in blocksc for isign = (-1,1), input: all, output: f, gc for isign = -1, approximate flop count: M*(5*log2(M) + 10)/nvpc for isign = 1,  approximate flop count: M*(5*log2(M) + 8)/nvpc where M = (nx/2)*ny, and nvp = number of procsc indx/indy = exponent which determines length in x/y direction,c where nx=2**indx, ny=2**indyc if isign = -1, an inverse fourier transform is performedc g(1:N,m,n,i) = sum(g(1:N,k,j,i)*exp(-sqrt(-1)*2pi*m*k/ny))c if isign = 1, a forward fourier transform is performedc g(1:N,k,j,i) = sum(g(1:N,m,n,i)*exp(sqrt(-1)*2pi*m*k/ny))c kstrt = starting data block numberc kxpi = initial x index usedc kxpp = number of   indices usedc nyv = first dimension of gc kxp = number of data values per block in xc jblok = number of data blocks in xc ndim = leading dimension of arrays f and gc mixup = array of bit reversed addressesc sct = sine/cosine tablec nxhyd = maximum of (nx/2,ny)c nxyhd = one half of maximum of (nx,ny)c the real data is stored in a complex array of length nx/2, nyc with the odd/even x points stored in the real/imaginary parts.c in complex notation, fourier coefficients are stored as follows:c g(k,j,i) = mode jj-1,k-1, where jj = j + kxp*(i - 1)c 1 <= jj <= nx/2 and 1 <= k <= ny, except forc g(k,1,1) = mode nx/2,k-1, where ny/2+2 <= k <= ny, andc imaginary part of g(1,1,1) = real part of mode nx/2,0 andc imaginary part of g(ny/2+1,1,1) = real part of mode nx/2,ny/2c written by viktor k. decyk, uclac parallel, RISC optimized version      implicit none      integer isign, mixup, indx, indy, kstrt, kxpi, kxpp, nyv      integer kxp, jblok, ndim, nxhyd, nxyhd      complex g, sct      dimension g(ndim,nyv,kxp,jblok)      dimension mixup(nxhyd), sct(nxyhd)c local data      integer indx1, indx1y, nx, nxh, ny, nyh, ny2      integer nxy, nxhy, ks, kxpt, j, k, nry      integer l, i, m, ns, ns2, km, kmr, k1, k2, j1, j2, jj      complex s, t1      if (isign.eq.0) return      indx1 = indx - 1      indx1y = max0(indx1,indy)      nx = 2**indx      nxh = nx/2      ny = 2**indy      nyh = ny/2      ny2 = ny + 2      nxy = max0(nx,ny)      nxhy = 2**indx1y      ks = kstrt - 2      kxpt = kxpi + kxpp - 1      if (kstrt.gt.nxh) return      if (isign.gt.0) go to 140c inverse fourier transform      nry = nxhy/ny      do 40 l = 1, jblokc bit-reverse array elements in y      do 30 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 30      do 20 j = kxpi, kxpt      do 10 jj = 1, ndim      t1 = g(jj,k1,j,l)      g(jj,k1,j,l) = g(jj,k,j,l)      g(jj,k,j,l) = t1   10 continue   20 continue   30 continue   40 continuec then transform in y      nry = nxy/ny      do 100 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 90 l = 1, jblok      do 80 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 70 j = 1, ns      j1 = j + k1      j2 = j + k2      s = sct(1+kmr*(j-1))      do 60 i = kxpi, kxpt      do 50 jj = 1, ndim      t1 = s*g(jj,j2,i,l)      g(jj,j2,i,l) = g(jj,j1,i,l) - t1      g(jj,j1,i,l) = g(jj,j1,i,l) + t1   50 continue   60 continue   70 continue   80 continue   90 continue  100 continuec unscramble modes kx = 0, nx/2      do 130 l = 1, jblok      if ((l+ks).gt.0) go to 130      do 120 k = 2, nyh      if (kxpi.eq.1) then         do 110 jj = 1, ndim         s = g(jj,ny2-k,1,l)         g(jj,ny2-k,1,l) = .5*cmplx(aimag(g(jj,k,1,l) + s),real(g(jj,k,1     1,l)- s))         g(jj,k,1,l) = .5*cmplx(real(g(jj,k,1,l) + s),aimag(g(jj,k,1,l)      1- s))  110    continue      endif  120 continue  130 continue      returnc forward fourier transformc scramble modes kx = 0, nx/2  140 nry = nxhy/ny      do 210 l = 1, jblok      if ((l+ks).gt.0) go to 170      do 160 k = 2, nyh      if (kxpi.eq.1) then         do 150 jj = 1, ndim         s = cmplx(aimag(g(jj,ny2-k,1,l)),real(g(jj,ny2-k,1,l)))         g(jj,ny2-k,1,l) = conjg(g(jj,k,1,l) - s)         g(jj,k,1,l) = g(jj,k,1,l) + s  150    continue      endif  160 continuec bit-reverse array elements in y  170 do 200 k = 1, ny      k1 = (mixup(k) - 1)/nry + 1      if (k.ge.k1) go to 200      do 190 j = kxpi, kxpt      do 180 jj = 1, ndim      t1 = g(jj,k1,j,l)      g(jj,k1,j,l) = g(jj,k,j,l)      g(jj,k,j,l) = t1  180 continue  190 continue  200 continue  210 continuec first transform in y      nry = nxy/ny      do 270 m = 1, indy      ns = 2**(m - 1)      ns2 = ns + ns      km = nyh/ns      kmr = km*nry      do 260 l = 1, jblok      do 250 k = 1, km      k1 = ns2*(k - 1)      k2 = k1 + ns      do 240 j = 1, ns      j1 = j + k1      j2 = j + k2      s = conjg(sct(1+kmr*(j-1)))      do 230 i = kxpi, kxpt      do 220 jj = 1, ndim      t1 = s*g(jj,j2,i,l)      g(jj,j2,i,l) = g(jj,j1,i,l) - t1      g(jj,j1,i,l) = g(jj,j1,i,l) + t1  220 continue  230 continue  240 continue  250 continue  260 continue  270 continue      return      endc-----------------------------------------------------------------------      subroutine PSWAPC2N(f,s,isign,nxh,kypi,kypt,nxvh,kypd,kblok,ndim)c this subroutine swaps components for multiple fftsc f = input  arrayc s = scratch arrayc isign = (-1,1) = swap (real-to-complex,complex-to-real)c nxh = complex dimension in x directionc kypi/kypt = initial/final y index usedc nxvh = half of the second dimension of fc kypd = third dimension of fc kblok = number of data blocks in yc ndim = leading dimension of array f      implicit none      integer isign, nxh, kypi, kypt, nxvh, kypd, kblok, ndim      real f, s      dimension f(ndim,2*nxvh,kypd,kblok), s(2*ndim*nxvh)c local data      integer i, j, k, l, ioffc swap complex componentsc real to complex      if (isign.lt.0) then         do 70 l = 1, kblok         do 60 k = kypi, kypt         do 20 j = 1, nxh         ioff = 2*ndim*(j - 1)         do 10 i = 1, ndim         s(2*i+ioff-1) = f(i,2*j-1,k,l)         s(2*i+ioff) = f(i,2*j,k,l)   10    continue   20    continue         do 50 j = 1, nxh         ioff = 2*ndim*(j - 1)         do 30 i = 1, ndim         f(i,2*j-1,k,l) = s(i+ioff)   30    continue         ioff = ioff + ndim         do 40 i = 1, ndim         f(i,2*j,k,l) = s(i+ioff)   40    continue   50    continue   60    continue   70    continue      else if (isign.gt.0) thenc swap complex components         do 140 l = 1, kblok         do 130 k = kypi, kypt         do 100 j = 1, nxh         ioff = 2*ndim*(j - 1)         do 80 i = 1, ndim         s(i+ioff) = f(i,2*j-1,k,l)   80    continue         ioff = ioff + ndim         do 90 i = 1, ndim         s(i+ioff) = f(i,2*j,k,l)   90    continue  100    continue         do 120 j = 1, nxh         ioff = 2*ndim*(j - 1)         do 110 i = 1, ndim         f(i,2*j-1,k,l) = s(2*i+ioff-1)         f(i,2*j,k,l) = s(2*i+ioff)  110    continue  120    continue  130    continue  140    continue      endif      return      end