c * * * periodic 2d electrostatic particle simulation kernel code * * *c this is a simple 2d skeleton particle-in-cell code designed forc exploring new computer architectures.  it contains the critical piecesc needed for depositing charge, advancing particles, and solving thec field.  the code moves only electrons, with periodic electrostaticc forces obtained by solving poisson's equation with fast fourierc transforms.  the only diagnostic is particle and field energy.c portable gcpic kernel code, using algorithm described in:c p. c. liewer and v. k. decyk, j. computational phys. 85, 302 (1989).c written by viktor k. decyk, uclac for mpi distributed memory computersc update: august 20, 2004      program beps2kc indx/indy = exponent which determines length in x/y direction,c where nx=2**indx, ny=2**indyc npx/npy = initial number of particles distributed in x/y directionc     parameter( indx =   5, indy =   6, npx =      96, npy =     192)      parameter( indx =   6, indy =   7, npx =     384, npy =     768)c     parameter( indx =   7, indy =   8, npx =    1280, npy =    2560)c npxb/npyb = initial number of particles in beam in x/y directionc     parameter( npxb =  32, npyb =  64)      parameter( npxb = 128, npyb = 256)c     parameter( npxb = 384, npyb = 768)c tend = time at end of simulation, in units of plasma frequencyc dt = time interval between successive calculations      parameter( tend =  65.000, dt = 0.2000000e+00)c vty = thermal velocity of electrons in y directionc vtdy = thermal velocity of beam electrons in y direction      parameter( vty =   1.000, vtdy =   0.500)c avdy = absolute value of drift velocity of beam electrons y direction      parameter( avdy =   5.000)c indnvp = exponent determining number of real or virtual processorsc indnvp must be <= indyc idps = number of partition boundariesc idimp = dimension of phase space = 4c mshare = (0,1) = (no,yes) architecture is shared memory      parameter( indnvp =   2, idps =    2, idimp =   4, mshare =   0)c np = total number of electrons in simulation      parameter(npxy=npx*npy,npxyb=npxb*npyb,np=npxy+npxyb)      parameter(nx=2**indx,ny=2**indy,nxh=nx/2,nyh=ny/2)      parameter(nxv=nx+2,nyv=ny+2,nxvh=nxv/2)      parameter(nxe=nx+4,nxeh=nxe/2)c nloop = number of time steps in simulation      parameter(nloop=tend/dt+.0001)c nvp = number of real or virtual processors, nvp = 2**indnvpc nblok = number of particle partitions      parameter(nvp=2**indnvp,nblok=1+mshare*(nvp-1))c npav = average number of particles per processorc npmax = maximum number of particles in each partitionc nypmx = maximum size of particle partition, including guard cells.c     parameter(npav=np/nvp,npmax=npav*1.01+7000,nypmx=(ny-1)/nvp+4)      parameter(npav=np/nvp,npmax=npav*4,nypmx=(ny+ny-1)/nvp+4)      parameter(nxvyp=nxv*nypmx,nxeyp=nxe*nypmx)c kyp = number of complex grids in each field partition in y directionc kxp = number of complex grids in each field partition in x direction      parameter(kyp=(ny-1)/nvp+1,kxp=(nxh-1)/nvp+1)c kyb = number of processors in yc kxb = number of processors in x      parameter(kyb=ny/kyp,kxb=nxh/kxp,jkmx=kxb*(kyb/kxb)+kyb*(kxb/kyb))c kxyb = maximum(kxb,kyb)      parameter(kxyb=jkmx/(2-kxb/jkmx-kyb/jkmx))c kblok = number of field partitions in y direction      parameter(kbmin=1+(1-mshare)*(kxyb/kxb-1))      parameter(kblok=1+mshare*(ny/kyp-1))c jblok = number of field partitions in x direction      parameter(jbmin=1+(1-mshare)*(kxyb/kyb-1))      parameter(jblok=1+mshare*(nxh/kxp-1))c ngds = number of guard cells      parameter(ngds=3*((idps-1)/2+1))c nxyh = maximum(nx,ny)/2c nxhy = maximum(nx/2,ny)      parameter(nmx=nx*(ny/nx)+ny*(nx/ny),nxy=nmx/(2-nx/nmx-ny/nmx))      parameter(nxyh=nxy/2,nmxh=nxh*(ny/nxh)+ny*(nxh/ny))      parameter(nxhy=nmxh/(2-nxh/nmxh-ny/nmxh))c nbmax = size of buffer for passing particles between processorsc     parameter(nbmax=1+(2*(npxy*vty+npxyb*vtdy)+1.4*npxyb*avdy)*dt/ny)      parameter(nbmax=50000)c ntmax = size of hole array for particles leaving processors      parameter(ntmax=2*nbmax)c npd = size of scratch buffers for vectorized charge deposition      parameter(npd=128,nine=9)c dimensions for sorting arraysc     parameter(nypm1=(ny-1)/nvp+2)      parameter(nypm1=(ny+ny-1)/nvp+2)c ipbc = particle boundary condition = (0,1,2,3) =c (none,2d periodic,2d reflecting,mixed reflecting/periodic)      parameter(ipbc=2)      complex qt, fxyt, ffc, bs, br, sct      common /large/ partc part(1,n,l) = position x of particle n in partition lc part(2,n,l) = position y of particle n in partition lc part(3,n,l) = velocity vx of particle n in partition lc part(4,n,l) = velocity vy of particle n in partition l      dimension part(idimp,npmax,nblok)c maskp = scratch array for particle addressesc     dimension maskp(npmax,nblok)c in real space, qe(j+1,k,l) = charge density at grid point (j,kk)c where kk = k + noff(l) - 1      dimension qe(nxe,nypmx*kbmin,kblok)c in real space, fxye(i,j+1,k,l) = i component of force/charge at c grid point (j,kk)c in other words, fxye are the convolutions of the electric fieldc over the particle shape, where kk = k + noff(l) - 1      dimension fxye(2,nxe,nypmx*kbmin,kblok)c qt(k,j,l) = complex charge density for fourier mode jj-1,k-1c fxyt(1,k,j,l) = x component of force/charge for fourier mode jj-1,k-1c fxyt(2,k,j,l) = y component of force/charge for fourier mode jj-1,k-1c where jj = j + kxp*(l - 1)      dimension qt(nyv,kxp,jblok)      dimension fxyt(2,nyv,kxp,jblok)c ffc = form factor array for poisson solver      dimension ffc(nyh,kxp,jblok)c mixup, sct = arrays for fft      dimension mixup(nxhy), sct(nxyh)c bs, br = complex buffer arrays for transposec     dimension bs(2,kxp,kyp,kblok), br(2,kxp,kyp,jblok)c msid, mrid = scratch arrays for identifying asynchronous messagesc     dimension msid(kxb), mrid(kyb)c edges(1,l) = lower boundary of particle partition lc edges(2,l) = upper boundary of particle partition l      dimension edges(idps,nblok)c nyp(l) = number of primary gridpoints in particle partition l.c noff(l) = lowermost global gridpoint in particle partition l.      dimension nyp(nblok), noff(nblok)c npp(l) = number of particles in partition lc nps(l) = starting address of particles in partition l      dimension npp(nblok), nps(nblok)c sbufl = buffer for particles being sent to lower processorc sbufr = buffer for particles being sent to upper processor      dimension sbufl(idimp,nbmax,nblok), sbufr(idimp,nbmax,nblok)c rbufl = buffer for particles being received from lower processorc rbufr = buffer for particles being received from upper processor      dimension rbufl(idimp,nbmax,nblok), rbufr(idimp,nbmax,nblok)c ihole = location of holes left in particle arrays      dimension ihole(ntmax,nblok)c jsl(idps,l) = number of particles going down in particle partition lc jsr(idps,l) = number of particles going up in particle partition l      dimension jsl(idps,nblok), jsr(idps,nblok)c jss(idps,l) = scratch array for particle partition lc scr(j,ngds,l) = complex scratch array for particle partition l      dimension jss(idps,nblok), scr(nxe,ngds,nblok)      dimension scs(2*nxe,nblok)c nn = scratch address array for vectorized charge depositionc amxy = scratch weight array for vectorized charge depositionc     dimension nn(npd,nine,nblok), amxy(npd,nine,nblok)c wtot = total energy      dimension wtot(3), work(3)c sorting arrays      dimension pt(npmax,nblok), ip(npmax,nblok), npic(nypm1,nblok)c scratch data for moving field partitions      dimension g(nxe,nypmx,kblok), h(2,nxe,nypmx,kblok)      dimension nyps(nblok), noffs(nblok), nypd(nblok), noffd(nblok)c scratch data for repartitioning      dimension edg(nypm1,nblok), eds(nypm1,nblok)      dimension eg(idps,nblok), es(idps,nblok), et2(2*idps,nblok)c non-uniform plasma      dimension qi(nxe,nypmx*kbmin,kblok)      real FGDISTR1      external FGDISTR1      character*12 labelc debug      dimension nypg(nvp), noffg(nvp)c end debugc potential and display      complex fxt, fyt      dimension fx(nxe,nypmx,kblok), fy(nxe,nypmx,kblok)      dimension fxt(nyv,kxp,jblok), fyt(nyv,kxp,jblok)      dimension pot(nxe,nypmx,kblok)      dimension lf(nxe,nypmx,kblok)c      parameter(indx1=indx+1,indy1=indy+1,nx2=2*nx,ny2=2*ny,nx2e=2*nxe)      parameter(nxhy2=2*nxhy,nxyh2=2*nxyh)      parameter(kxp2=(nx-1)/nvp+1,j2blok=1+mshare*(nx/kxp2-1))      parameter(kxb2=nx/kxp2)      parameter(jkmx1=kxb2*(kyb/kxb2)+kyb*(kxb2/kyb))      parameter(kxyb1=jkmx1/(2-kxb2/jkmx1-kyb/jkmx1))      parameter(kbmin1=1+(1-mshare)*(kxyb1/kxb2-1))c      complex qt1, fxyt1, fxt1, fyt1, ffb, sct1, bs1, br1      dimension q1(nx2e,kyp*kbmin1,kblok)      dimension fxy1(2,nx2e,kyp*kbmin1,kblok)      dimension pot1(nx2e,kyp*kbmin1,kblok)      dimension qt1(nyv,kxp2,j2blok), fxyt1(2,nyv,kxp2,j2blok)      dimension fxt1(nyv,kxp2,j2blok), fyt1(nyv,kxp2,j2blok)      dimension ffb(nyh,kxp2,j2blok)      dimension mixup1(nxhy2), sct1(nxyh2)c     dimension bs1(2,kxp2,kyp,kblok), br1(2,kxp2,kyp,jblok)c      parameter(kyp2=(ny2-1)/nvp+1,k2blok=1+mshare*(ny2/kyp2-1))      parameter(kyb2=ny2/kyp2)      parameter(jkmx2=kxb2*(kyb2/kxb2)+kyb2*(kxb2/kyb2))      parameter(kxyb2=jkmx2/(2-kxb2/jkmx2-kyb2/jkmx2))      parameter(kbmin2=1+(1-mshare)*(kxyb2/kxb2-1))c      complex qt2, fxyt2, fxt2, fyt2, ffd, sct2, bs2, br2      dimension q2(nx2e,kyp2*kbmin2,k2blok)      dimension fxy2(2,nx2e,kyp2*kbmin2,k2blok)      dimension pot2(nx2e,kyp2*kbmin2,k2blok)      dimension qt2(ny2,kxp2,j2blok), fxyt2(2,ny2,kxp2,j2blok)      dimension fxt2(ny2,kxp2,j2blok), fyt2(ny2,kxp2,j2blok)      dimension ffd(ny,kxp2,j2blok)      dimension mixup2(nxhy2), sct2(nxyh2)c     dimension bs2(2,kxp2,kyp2,k2blok), br2(2,kxp2,kyp2,j2blok)c  991 format (5h T = ,i7)  992 format (19h * * * q.e.d. * * *)  993 format (34h field, kinetic, total energies = ,3e14.7)c vtx = thermal velocity of electrons in x directionc qme = charge on electron, in units of ec vdx/vdy = drift velocity of beam electrons in x/y directionc vtdx = thermal velocity of beam electrons in x direction      data vtx,qme,vdx,vdy,vtdx /1.,-1.,0.,5.,.5/c ax/ay = half-width of particle in x/y direction      data ax,ay /.866667,.866667/c ntpose = (0,1) = (no,yes) input, output data are transposed in pfft2r      data ntpose /1/c debugc     data nypg /48,16,16,48/, noffg /0,48,64,80/c end debugc initialize for parallel processing      call ppinit(idproc,nvp)      kstrt = idproc + 1      if (kstrt.eq.1) then         open(unit=6,file='output2',form='formatted',status='unknown')      endifc initialize timer      call TIMERA(-1,'init    ',time)c initialize constants      itime = 0      nypmin = kyp      nypmax = kyp      nterf = 0      nterg = 1      if (nypmin.eq.1) nterg = 0      aipav = 1.0/real(npav)      qbme = qme      zero = 0.      if (ipbc.eq.1) then         affp = float(nx*ny)/float(np)      else if (ipbc.eq.2) then         affp = float((nx-2)*(ny-2))/float(np)      else if (ipbc.eq.3) then         affp = float((nx-2)*ny)/float(np)      endif      qi0 = -qme/affpc calculate partition variables      call dcomp2(edges,nyp,noff,ny,kstrt,nvp,idps,nblok)c prepare fft tables      isign = 0c     call PFFT2R(qe,qt,bs,br,isign,ntpose,mixup,sct,indx,indy,kstrt,nxec    1h,nyv,kxp,kyp,nypmx,jblok,kblok,nxhy,nxyh)      call PFFT2RX(qe,qt,isign,ntpose,mixup,sct,indx,indy,kstrt,nxeh,nyv     1,kxp,kyp,nypmx,jblok,kblok,nxhy,nxyh)c calculate form factors      call PPOIS22(qt,fxyt,isign,ffc,ax,ay,affp,we,nx,ny,kstrt,nyv,kxp,j     1blok,nyh)cc     call PFFT2R(q2,qt2,bs2,br2,isign,ntpose,mixup2,sct2,indx1,indy1,ksc    1trt,nxe,ny2,kxp2,kyp2,kyp2,j2blok,k2blok,nxhy2,nxyh2)      call PFFT2RX(q2,qt2,isign,ntpose,mixup2,sct2,indx1,indy1,kstrt,nxe     1,ny2,kxp2,kyp2,kyp2,j2blok,k2blok,nxhy2,nxyh2)      call PPOISDX22(qt2,fxyt2,isign,ffd,ax,ay,affp,wf,nx,ny,kstrt,ny2,k     1xp2,j2blok,ny)cc     call PFFT2R(q1,qt1,bs1,br1,isign,ntpose,mixup1,sct1,indx1,indy,kstc    1rt,nxe,nyv,kxp2,kyp,kyp,j2blok,kblok,nxhy2,nxyh2)      call PFFT2RX(q1,qt1,isign,ntpose,mixup1,sct1,indx1,indy,kstrt,nxe,     1nyv,kxp2,kyp,kyp,j2blok,kblok,nxhy2,nxyh2)      call PPOISMX22(qt1,fxyt1,isign,ffb,ax,ay,affp,we,nx,ny,kstrt,nyv,k     1xp2,j2blok,nyh)c      call GROPEN      call SETNPLT(3,irc)      call STPALIT(1)cc initialize density profile and velocity distribution      if (ipbc.eq.2) vdy = 0.c background electrons      do 130 l = 1, nblok      nps(l) = 1      npp(l) = 0  130 continuec     if (npxy.gt.0) call PISTR2(part,edges,npp,nps,vtx,vty,zero,zero,npc    1x,npy,nx,ny,idimp,npmax,nblok,idps,ipbc,ierr)      ang = 10000.0      wi = 0.1      y0 = real(nyh)      if (npxy.gt.0) then         call PFDISTR2(part,nps,FGDISTR1,zero,zero,zero,FGDISTR1,ang,wi,     1y0,npx,npy,nx,ny,idimp,npmax,nblok,kstrt,nvp,ipbc,ierr)         if (ierr.ne.0) then            call ppexit            stop         endif         call PVDISTR2(part,npp,nps,vtx,vty,zero,zero,npx,npy,idimp,npma     1x,nblok,kstrt,nvp,ierr)         if (ierr.ne.0) then            call ppexit            stop         endif      endifc beam electrons      do 140 l = 1, nblok      nps(l) = npp(l) + 1  140 continuec     if (npxyb.gt.0) call PISTR2(part,edges,npp,nps,vtdx,vtdy,vdx,vdy,nc    1pxb,npyb,nx,ny,idimp,npmax,nblok,idps,ipbc,ierr)      if (npxyb.gt.0) then         call PFDISTR2(part,nps,FGDISTR1,zero,zero,zero,FGDISTR1,ang,wi,     1y0,npxb,npyb,nx,ny,idimp,npmax,nblok,kstrt,nvp,ipbc,ierr)         if (ierr.ne.0) then            call ppexit            stop         endif         call PVDISTR2(part,npp,nps,vtdx,vtdy,vdx,vdy,npxb,npyb,idimp,np     1max,nblok,kstrt,nvp,ierr)         if (ierr.ne.0) then            call ppexit            stop         endif      endifc debug: redefine spatial regionsc     ks = kstrt - 2c     do 150 l = 1, nblokc     nyp(l) = nypg((l+ks)+1)c     noff(l) = noffg((l+ks)+1)c     edges(1,l) = noff(l)c     edges(2,l) = noff(l) + nyp(l)c 150 continuec find new partition analytically      call FEDGES2(edges,noff,nyp,FGDISTR1,ang,wi,y0,ny,nypmin,nypmax,ks     1trt,nvp,nblok,idps,ipbc)      if ((nypmin.ge.1).and.((nypmax+3).le.nypmx)) then         nterg = 1         if (nypmin.eq.1) nterg = 0      else         write (6,*) 'Field size error: nypmin,nypmax=',nypmin,nypmax         call ppexit         stop      endifc move particles into appropriate spatial regions      call pmove2(part,edges,npp,sbufr,sbufl,rbufr,rbufl,ihole,jsr,jsl,j     1ss,ny,kstrt,nvp,idimp,npmax,nblok,idps,nbmax,ntmax,ierr)      if (ierr.ne.0) then         call ppexit         stop      endifc count the number of particles per cellc     isign = 1c     call PCOUNT2YL(part,isign,npic,npp,noff,nyp,idimp,npmax,nblok,nypmc    11)c determine repartitioning boundariesc     call REPARTD2(edges,edg,eds,eg,es,et2,npic,noff,nyp,npav,nypmin,nyc    1pmax,kstrt,nvp,nblok,idps,nypm1)c     if ((nypmin.ge.1).and.((nypmax+3).le.nypmx)) thenc        nterg = 1c        if (nypmin.eq.1) nterg = 0c     elsec        write (6,*) 'Field size error: nypmin,nypmax=',nypmin,nypmaxc        call ppexitc        stopc     endifc move particles into appropriate spatial regionsc     call pmove2(part,edges,npp,sbufr,sbufl,rbufr,rbufl,ihole,jsr,jsl,jc    1ss,ny,kstrt,nvp,idimp,npmax,nblok,idps,nbmax,ntmax,ierr)c     if (ierr.ne.0) thenc        call ppexitc        stopc     endifc set ion backgroundc     if (ipbc.eq.1) thenc        call PSGUARD2(qe,nyp,qi0,nx,nxe,nypmx,kblok)c     else if (ipbc.eq.2) thenc        call PLSGUARD2X(qe,kstrt,nvp,noff,nyp,qi0,nx,ny,1,1,nxe,nypmx,kc    1blok)c        call PLSGUARD2(qe,kstrt,nvp,nyp,qi0,nx,1,1,nxe,nypmx,kblok)c     else if (ipbc.eq.3) thenc        call PMSGUARD2(qe,nyp,qi0,nx,1,nxe,nypmx,kblok)c     endifc set ion background      if (ipbc.eq.1) then         call PSGUARD2(qi,nyp,zero,nx,nxe,nypmx,kblok)      else if (ipbc.eq.2) then         call PLSGUARD2X(qi,kstrt,nvp,noff,nyp,zero,nx,ny,1,1,nxe,nypmx,     1kblok)c        call PLSGUARD2(qi,kstrt,nvp,nyp,zero,nx,1,1,nxe,nypmx,kblok)      else if (ipbc.eq.3) then         call PMSGUARD2(qi,nyp,zero,nx,1,nxe,nypmx,kblok)      endif      call PGPOST2(part,qi,npp,noff,abs(qme),idimp,npmax,nblok,nxe,nypmx     1)      if (ipbc.eq.1) then         call PAGUARD2X(qi,nyp,nx,nxe,nypmx,kblok)c        call PAGUARD2(qi,scr,kstrt,nvp,nxe,nypmx,nblok,kyp,kblok,ngds)         call PNAGUARD2(qi,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngds     1,nterg)      else if (ipbc.eq.2) then         call PLAGUARD2X(qi(2,1,1),nyp,nx-2,nxe,nypmx,kblok)c        call PLAGUARD2(qi,scr,kstrt,nvp,nx,nxe,nypmx,kyp,kblok,ngds)         call PNLAGUARD2(qi,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngd     1s,nterg)c        call PLAGUARDS2(qi,kstrt,nvp,nx,nxe,nypmx,kyp,kblok)         call PNLAGUARDS2(qi,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,nterg)      else if (ipbc.eq.3) then         call PLAGUARD2X(qi(2,1,1),nyp,nx-2,nxe,nypmx,kblok)c        call PAGUARD2(qi,scr,kstrt,nvp,nxe,nypmx,nblok,kyp,kblok,ngds)         call PNAGUARD2(qi,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngds     1,nterg)      endifc     if (ipbc.eq.1) thenc        call PSGUARD2(qi,nyp,qi0,nx,nxe,nypmx,kblok)c     else if (ipbc.eq.2) thenc        call PLSGUARD2X(qi,kstrt,nvp,noff,nyp,qi0,nx,ny,1,1,nxe,nypmx,kc    1blok)c        call PLSGUARD2(qi,kstrt,nvp,nyp,qi0,nx,1,1,nxe,nypmx,kblok)c     else if (ipbc.eq.3) thenc        call PMSGUARD2(qi,nyp,qi0,nx,1,nxe,nypmx,kblok)c     endifc initialize charge density to background      do 330 l = 1, kblok      do 320 k = 1, nypmx      do 310 j = 1, nxe      qe(j,k,l) = qi(j,k,l)  310 continue  320 continue  330 continuec deposit charge for initial distributionc     call PDOST2(part,q,npp,noff,qme,nx,idimp,npmax,nblok,nxv,nypmx)      call PGPOST2(part,qe,npp,noff,qme,idimp,npmax,nblok,nxe,nypmx)c add guard cells      if (ipbc.eq.1) then         call PAGUARD2X(qe,nyp,nx,nxe,nypmx,kblok)c        call PAGUARD2(qe,scr,kstrt,nvp,nxe,nypmx,nblok,kyp,kblok,ngds)         call PNAGUARD2(qe,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngds     1,nterg)      else if (ipbc.eq.2) then         call PLAGUARD2X(qe(2,1,1),nyp,nx-2,nxe,nypmx,kblok)c        call PLAGUARD2(qe,scr,kstrt,nvp,nx,nxe,nypmx,kyp,kblok,ngds)         call PNLAGUARD2(qe,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngd     1s,nterg)c        call PLAGUARDS2(qe,kstrt,nvp,nx,nxe,nypmx,kyp,kblok)         call PNLAGUARDS2(qe,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,nterg)      else if (ipbc.eq.3) then         call PLAGUARD2X(qe(2,1,1),nyp,nx-2,nxe,nypmx,kblok)c        call PAGUARD2(qe,scr,kstrt,nvp,nxe,nypmx,nblok,kyp,kblok,ngds)         call PNAGUARD2(qe,scr,scs,nyp,kstrt,nvp,nx,nxe,nypmx,nblok,ngds     1,nterg)      endif      call TIMERA(1,'init    ',time)      call TIMERA(-1,'main    ',time)cc * * * start main iteration loop * * *c  500 if (nloop.le.itime) go to 2000      if (kstrt.eq.1) write (6,991) itime      write (label,991) itime      call LOGNAME(label)c move data to uniform field partition.      isign = -1      call PFMOVE2(qe(1,2,1),g,noff,nyp,noffs,nyps,noffd,nypd,jsr,jsl,is     1ign,kyp,kstrt,nxe,nypmx,nblok,idps,nterf,ierr)      if (ierr.ne.0) then         call ppexit         stop      endifcc dirichlet boundary conditions      if (ipbc.eq.2) thenc      call PDBLSIN2D(qe(2,2,1),q2,nx,ny,kstrt,nxe,kyp,nypmx,kyp2,kblok,k     12blok)c transform charge to fourier space      isign = -1      call PFFT2RX(q2,qt2,isign,ntpose,mixup2,sct2,indx1,indy1,kstrt,nxe     1,ny2,kxp2,kyp2,kyp2,j2blok,k2blok,nxhy2,nxyh2)c solve for electric fields      isign = -1      call PPOISDX22(qt2,fxyt2,isign,ffd,ax,ay,affp,we,nx,ny,kstrt,ny2,k     1xp2,j2blok,ny)      isign = 1      call PFFT2RX2